-- ----------------------------
--  Table structure for `book`
-- ----------------------------
DROP TABLE IF EXISTS `book`;
CREATE TABLE `book` (
  `create_time` int(11) DEFAULT NULL,
  `status` smallint(6) DEFAULT NULL,
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `author` varchar(255) DEFAULT NULL COMMENT '作者，多个作者序列化',
  `category` varchar(100) DEFAULT NULL COMMENT '分类',
  `image` varchar(100) DEFAULT NULL COMMENT '书籍图片',
  `isbn` varchar(50) DEFAULT NULL COMMENT '书籍isbn',
  `pages` int(11) DEFAULT NULL COMMENT '书籍页数',
  `price` float DEFAULT NULL COMMENT '书籍价格',
  `pubdate` varchar(20) DEFAULT NULL COMMENT '发布时间',
  `publisher` varchar(50) DEFAULT NULL COMMENT '出版社',
  `subtitle` varchar(50) DEFAULT NULL COMMENT '主题标题',
  `summary` text COMMENT '描述',
  `title` varchar(100) DEFAULT NULL COMMENT '书名',
  `translator` varchar(255) DEFAULT NULL COMMENT '译者',
  `fav_nums` int(11) DEFAULT '0' COMMENT '收藏数',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=108 DEFAULT CHARSET=utf8mb4;

-- ----------------------------
--  Records of `book`
-- ----------------------------
BEGIN;
INSERT INTO `book` VALUES ('1534620602', '1', '1', '[\"[\\u65e5]\\u9e1f\\u5c71\\u660e\"]', '日本漫画', 'https://img1.doubanio.com/lpic/s28671937.jpg', '9787600100354', '0', null, '2005-7-1', '中国少年儿童出版社', '', '《DRAGON BALL》译名《龙珠》（又名：七龙珠）是日本著名漫画家鸟山明的得意作品，1984年登场，1992年又推出『龙珠』续集。这部长篇巨作在『少年跳跃』上连载7年。\\n故事讲述了孙悟空（当然不是我们神化中的那个齐天大圣喽）父子和那7颗能满足人们心愿的龙珠，把读者从山村引到城市，从地球引到外星，又从现在引到未来。\\n根据《龙珠》的漫画故事，还推出了《龙珠》的系列动画片，无论是TV版还是剧场版，都吸引了无数的龙珠迷，掀起了一股股“龙珠”的热潮。\\n龙珠的舞台，虽说是带有中国色彩的，但是也并非就限定在中国；时代也一样，并没有把它固定在某一个具体时间。整个故事架构很简单，至于一些细节和结局，我想任其自由发展。这么一来，连我自己也会想[故事下一步会怎么发展啊]，并为之捏了一把汗。因为无论怎么发挥都可以，所以乐在其中。\\n传说中，地球四处散落着7颗龙珠。如果谁将他们收集起来就可以实现自己的愿望，人们为了得到它而不断的你争我夺……每年都会有一场以龙珠为奖品的《天下第一武道大会》……在地球的一个角落，生活着孙悟空这个茁壮的孩子，他的身份其实是赛亚人卡卡罗特。因为婴儿时的变身能力不够而被派往地球，其实是为了毁灭地球生物而变成殖民地，但他生来和平，丝毫不知自己的身世……这种战斗力超强的种族“赛亚人”与宇宙中另一些种族“那美克星人”等等间发生了无数惊险有趣又富有教育意义的故事……这就是日本著名漫画家“鸟山明”创造的《七龙珠》世界。', '龙珠(全42册)', '[\"\\u725f\\u7433\"]', '0'), ('1534620614', '1', '2', '[\"[\\u65e5]\\u9e1f\\u5c71\\u660e\"]', '日本漫画', 'https://img3.doubanio.com/lpic/s11231433.jpg', '9787500774860', '175', null, '2005-7', '中国少年儿童出版社', '孙悟空和伙伴们', '《龙珠(套装共42册)》是日本著名漫画家鸟山明的得意作品。相传，有七颗龙珠散落在世界的各个角落，当它们被聚集到一起时神龙就会出现，能够实现你的任何一个愿望。这个一个美丽的的传说引出了小悟空和他的朋友们寻找龙珠的故事。', '龙珠1', '[\"\\u725f\\u7433\"]', '0'), ('1534620659', '1', '3', '[\"\\u9648\\u5112\"]', '编程', 'https://img3.doubanio.com/lpic/s3435132.jpg', '9787121068744', '480', null, '2008-6', '电子工业出版社', '深度探索动态语言核心技术', '作为主流的动态语言，Python不仅简单易学、移植性好，而且拥有强大丰富的库的支持。此外，Python强大的可扩展性，让开发人员既可以非常容易地利用C/C++编写Python的扩展模块，还能将Python嵌入到C/C++程序中，为自己的系统添加动态扩展和动态编程的能力。.\\n为了更好地利用Python语言，无论是使用Python语言本身，还是将Python与C/C++交互使用，深刻理解Python的运行原理都是非常重要的。本书以CPython为研究对象，在C代码一级，深入细致地剖析了Python的实现。书中不仅包括了对大量Python内置对象的剖析，更将大量的篇幅用于对Python虚拟机及Python高级特性的剖析。通过此书，读者能够透彻地理解Python中的一般表达式、控制结构、异常机制、类机制、多线程机制、模块的动态加载机制、内存管理机制等核心技术的运行原理，同时，本书所揭示的动态语言的核心技术对于理解其他动态语言，如 Javascript、Ruby等也有较大的参考价值。..\\n本书适合于Python程序员、动态语言爱好者、C程序员阅读', 'Python源码剖析', '[]', '0'), ('1534620659', '1', '4', '[\"MarkPilgrim\"]', '编程', 'https://img3.doubanio.com/lpic/s29631790.jpg', '9781590593561', '413', null, '19 July, 2004', 'Apress', '', 'Dive Into Python 是为有经验的程序员编写的一本 Python 书。', 'Dive Into Python', '[]', '0'), ('1534620659', '1', '5', '[\"MarkPilgrim\"]', '编程', 'https://img3.doubanio.com/lpic/s4059293.jpg', '9781430224150', '360', null, '2009-11-6', 'Apress', '', 'Mark Pilgrim\'s Dive Into Python 3 is a hands-on guide to Python 3 (the latest version of the Python language) and its differences from Python 2. As in the original book, Dive Into Python, each chapter starts with a real, complete code sample, proceeds to pick it apart and explain the pieces, and then puts it all back together in a summary at the end.\\nThis book includes:\\n* Example programs completely rewritten to illustrate powerful new concepts now available in Python 3: sets, iterators, generators, closures, comprehensions, and much more\\n* A detailed case study of porting a major library from Python 2 to Python 3\\n* A comprehensive appendix of all the syntactic and semantic changes in Python 3\\nThis is the perfect resource for you if you need to port applications to Python 3, or if you like to jump into languages fast and get going right away.\\nWhat you\'ll learn\\n* Understand Python 3 code by seeing it broken down and explained\\n* Make full use of the latest Python features such as iterators, generators, closures, classes and comprehensions\\n* Refactor existing code to improve maintainability\\n* Learn how to serialize Python objects with the pickle protocol and JSON format\\n* Learn how to package your own Python libraries and upload them to the Python Package Index to share your projects with Python developers worldwide\\n* Use Python 3 to consume HTTP web services\\n* Port existing Python applications to Python 3 by following a case study for a major library\\nWho is this book for?\\n* Anyone who wants to learn the latest version of Python in a fast, hands-on fashion\\n* Existing Python programmers who want to learn quickly how to make the most of the features of the latest version of Python and port their code to it\\n* Programmers coming from other languages wanting a fast introduction to Python that gets them thinking about advanced concepts quickly', 'Dive Into Python 3', '[]', '2'), ('1534620659', '1', '6', '[\"P.J.Deitel\", \"J.P.Liperi\", \"B.A.Wiedermann\", \"H.M.Deitel\"]', '编程', 'https://img3.doubanio.com/lpic/s1326052.jpg', '9787302066422', '596', null, '2003-6', '清华大学出版社', '', '本书由全球著名的程序语言培训专家精心编著，解释了如何将Python用作常规用途，编写多层、客户机/服务器结构、数据库密集型、基于Internet和Web的应用程序。书中采用作者独创的“活代码”教学方式，层层揭示了Python这一程序设计语言的强大功能，并通过穿插在全书各处的屏幕输出和编程技巧与提示，帮助读者搭建良好的知识结构、养成良好的编程习惯、避免常见的编程错误以及写出高效、可靠的应用程序。', 'Python 编程金典', '[]', '0'), ('1534620659', '1', '7', '[\"Noah Gift\", \"Jeremy Jones\"]', '编程', 'https://img3.doubanio.com/lpic/s3355252.jpg', '9780596515829', '458', null, '2008-08-22', 'O\'Reilly Media', '', 'Python is an ideal language for solving problems, especially in Linux and Unix networks. With this pragmatic book, administrators can review various tasks that often occur in the management of these systems, and learn how Python can provide a more efficient and less painful way to handle them.\\nEach chapter in Python for Unix and Linux System Administration presents a particular administrative issue, such as concurrency or data backup, and presents Python solutions through hands-on examples. Once you finish this book, you\'ll be able to develop your own set of command-line utilities with Python to tackle a wide range of problems. Discover how this language can help you:\\n* Read text files and extract information\\n* Run tasks concurrently using the threading and forking options\\n* Get information from one process to another using network facilities\\n* Create clickable GUIs to handle large and complex utilities\\n* Monitor large clusters of machines by interacting with SNMP programmatically\\n* Master the IPython Interactive Python shell to replace or augment Bash, Korn, or Z-Shell\\n* Integrate Cloud Computing into your infrastructure, and learn to write a Google App Engine Application\\n* Solve unique data backup challenges with customized scripts\\n* Interact with MySQL, SQLite, Oracle, Postgres, Django ORM, and SQLAlchemy\\nWith this book, you\'ll learn how to package and deploy your Python applications and libraries, and write code that runs equally well on multiple Unix platforms. You\'ll also learn about several Python-related technologies that will make your life much easier.', 'Python for Unix and Linux System Administration', '[]', '0'), ('1534620659', '1', '8', '[\"MagnusLieHetland\"]', '编程', 'https://img3.doubanio.com/lpic/s4387251.jpg', '9787115230270', '471', null, '2010-7', '人民邮电出版社', '', '本书是经典教程的全新改版，作者根据Python 3.0版本的种种变化，全面改写了书中内容，做到既能“瞻前”也能“顾后”。本书层次鲜明、结构严谨、内容翔实，特别是在最后几章，作者将前面讲述的内容应用到了10个引人入胜的项目中，并以模板的形式介绍了项目的开发过程。本书既适合初学者夯实基础，又能帮助Python程序员提升技能，即使是 Python方面的技术专家，也能从书里找到令你耳目一新的东西。', 'Python基础教程', '[\"\\u53f8\\u7ef4\", \"\\u66fe\\u519b\\u5d34\", \"\\u8c2d\\u9896\\u534e\"]', '0'), ('1534620659', '1', '9', '[\"SwaroopCH\"]', '编程', 'https://img3.doubanio.com/lpic/s4612135.jpg', '9789577296467', '149', null, '2008-10-1', 'Lulu Marketplace', '简明 Python 教程', '\'A Byte of Python\' is a book on programming using the Python language. It serves as a tutorial or guide to the Python language for a beginner audience. If all you know about computers is how to save text files, then this is the book for you.', 'A Byte of Python', '[\"\\u6c88\\u6d01\\u5143\"]', '0'), ('1534620659', '1', '10', '[\"WesMcKinney\"]', '编程', 'https://img3.doubanio.com/lpic/s27275372.jpg', '9787111436737', '464', null, '2013-11-18', '机械工业出版社', '', '【名人推荐】\\n“科学计算和数据分析社区已经等待这本书很多年了：大量具体的实践建议，以及大量综合应用方法。本书在未来几年里肯定会成为Python领域中技术计算的权威指南。”\\n——Fernando Pérez　加州大学伯克利分校 研究科学家， IPython的创始人之一\\n【内容简介】\\n还在苦苦寻觅用Python控制、处理、整理、分析结构化数据的完整课程？本书含有大量的实践案例，你将学会如何利用各种Python库（包括NumPy、pandas、matplotlib以及IPython等）高效地解决各式各样的数据分析问题。\\n由于作者Wes McKinney是pandas库的主要作者，所以本书也可以作为利用Python实现数据密集型应用的科学计算实践指南。本书适合刚刚接触Python的分析人员以及刚刚接触科学计算的Python程序员。\\n•将IPython这个交互式Shell作为你的首要开发环境。\\n•学习NumPy（Numerical Python）的基础和高级知识。\\n•从pandas库的数据分析工具开始。\\n•利用高性能工具对数据进行加载、清理、转换、合并以及重塑。\\n•利用matplotlib创建散点图以及静态或交互式的可视化结果。\\n•利用pandas的groupby功能对数据集进行切片、切块和汇总操作。\\n•处理各种各样的时间序列数据。\\n•通过详细的案例学习如何解决Web分析、社会科学、金融学以及经•济学等领域的问题。', '利用Python进行数据分析', '[\"\\u5510\\u5b66\\u97ec\"]', '0'), ('1534620659', '1', '11', '[\"Luciano Ramalho\"]', '编程', 'https://img3.doubanio.com/lpic/s27935775.jpg', '9781491946008', '768', null, '2015-8-20', 'O\'Reilly Media', '', 'Learn how to write idiomatic, effective Python code by leveraging its best features. Python\'s simplicity quickly lets you become productive with it, but this often means you aren’t using everything the language has to offer. By taking you through Python’s key language features and libraries, this practical book shows you how to make your code shorter, faster, and more readable all at the same time—what experts consider Pythonic.\\nMany programmers who learn Python basics fall into the trap of reinventing the wheel because of past experience in other languages, and try to bend the language to patterns that don\'t really apply to it. Author Luciano Ramalho, a Python Software Foundation member and Python programmer for 15 years, helps you drop your accent from another language so you can code Python fluently.\\n•Learn practical applications of generators for database processing\\n•Rethink some design patterns in a Python context\\n•Examine attribute descriptors and when to use them: the key to ORMs\\n•Explore Pythonic objects: protocols versus interfaces, abstract base classes and multiple inheritance', 'Fluent Python', '[]', '0'), ('1534620659', '1', '12', '[\"\\u65af\\u7ef4\\u52a0\\u7279 (Al Sweigart)\"]', '编程', 'https://img3.doubanio.com/lpic/s29400403.jpg', '9787115422699', '390', null, '2016-7-1', '人民邮电出版社', '让繁琐工作自动化', '如今，人们面临的大多数任务都可以通过编写计算机软件来完成。Python是一种解释型、面向对象、动态数据类型的高级程序设计语言。通过Python编程，我们能够解决现实生活中的很多任务。\\n本书是一本面向实践的Python编程实用指南。本书的目的，不仅是介绍Python语言的基础知识，而且还通过项目实践教会读者如何应用这些知识和技能。本书的首部分介绍了基本Python编程概念，第二部分介绍了一些不同的任务，通过编写Python程序，可以让计算机自动完成它们。第二部分的每一章都有一些项目程序，供读者学习。每章的末尾还提供了一些习题和深入的实践项目，帮助读者巩固所学的知识。附录部分提供了所有习题的解答。\\n本书适合任何想要通过Python学习编程的读者，尤其适合缺乏编程基础的初学者。通过阅读本书，读者将能利用强大的编程语言和工具，并且会体会到Python编程的快乐。', 'Python编程快速上手', '[]', '0'), ('1534620659', '1', '13', '[\"Vamei\"]', '编程', 'https://img1.doubanio.com/lpic/s29166309.jpg', '9787121301995', '208', null, '2016-11-24', '电子工业出版社', '', '改编自Vamei博客的《Python快速教程》。本书以Python为样本，不仅介绍了编程的基本概念，还着重讲解编程语言的主流范式：面向过程、面向对象、面向函数。读者不仅可以轻松学会Python，以后再学习其他编程语言时也会更加容易。', '从Python开始学编程', '[]', '3'), ('1534620659', '1', '14', '[\"[\\u7f8e]MarkLutz\"]', '编程', 'https://img1.doubanio.com/lpic/s3952568.jpg', '9787111267768', '656', null, '2009-8', '机械工业出版社', '（第3版）', '《Python学习手册(第3版)》讲述了：Python可移植、功能强大、易于使用，是编写独立应用程序和脚本应用程序的理想选择。无论你是刚接触编程或者刚接触Python，通过学习《Python学习手册(第3版)》，你可以迅速高效地精通核心Python语言基础。读完《Python学习手册(第3版)》，你会对这门语言有足够的了解，从而可以在你所从事的任何应用领域中使用它。\\n《Python学习手册(第3版)》是作者根据过去10年用于教学而广为人知的培训课程的材料编写而成的。除了有许多详实说明和每章小结之外，每章还包括一个头脑风暴：这是《Python学习手册(第3版)》独特的一部分，配合以实用的练习题和复习题，让读者练习新学的技巧并测试自己的理解程度。\\n《Python学习手册(第3版)》包括：\\n类型和操作——深入讨论Python主要的内置对象类型：数字、列表和字典等。\\n语句和语法——在Python中输入代码来建立并处理对象，以及Python一般的语法模型。\\n函数——Python基本的面向过程工具，用于组织代码和重用。\\n模块——封装语句、函数以及其他工具，从而可以组织成较大的组件。\\n类和OOP——Python可选的面向对象编程工具，可用于组织程序代码从而实现定制和重用。\\n异常和工具——异常处理模型和语句，并介绍编写更大程序的开发工具。\\n讨论Python 3.0。\\n《Python学习手册(第3版)》让你对Python语言有深入而完整的了解，从而帮助你理解今后遇到的任何Python应用程序实例。如果你准备探索Google和YouTube为什么选中了Python，《Python学习手册(第3版)》就是你入门的最佳指南。', 'Python学习手册', '[\"\\u4faf\\u9756\"]', '0'), ('1534620659', '1', '15', '[\"Alex Martelli\", \"Anna Ravenscroft\", \"David Ascher\"]', '编程', 'https://img3.doubanio.com/lpic/s4357883.jpg', '9787115222664', '756', null, '2010-5-1', '人民邮电出版社', '（第2版）中文版', '本书介绍了Python应用在各个领域中的一些使用技巧和方法，从最基本的字符、文件序列、字典和排序，到进阶的面向对象编程、数据库和数据持久化、 XML处理和Web编程，再到比较高级和抽象的描述符、装饰器、元类、迭代器和生成器，均有涉及。书中还介绍了一些第三方包和库的使用，包括 Twisted、GIL、PyWin32等。本书覆盖了Python应用中的很多常见问题，并提出了通用的解决方案。书中的代码和方法具有很强的实用性，可以方便地应用到实际的项目中，并产生立竿见影的效果。尤为难得的是，本书的各位作者都具有丰富的业界实践经验，因此，本书不仅给出了对各种问题的解决方案，同时还体现了很多专家的思维方式和良好的编程习惯，与具体的细节性知识相比，这部分内容无疑是本书的精华。\\n本书适合具有一定Python基础的读者阅读参考。', 'Python Cookbook', '[\"\\u9ad8\\u94c1\\u519b\"]', '0'), ('1534620659', '1', '16', '[\"[\\u7f8e]WesleyJ\\u00b7Chun\"]', '编程', 'https://img3.doubanio.com/lpic/s3140466.jpg', '9787115178503', '654', null, '2008-06', '人民邮电出版社', '', '本书是Python开发者的完全指南——针对 Python 2.5全面升级\\n·学习专业的Python风格、最佳实践和好的编程习惯；\\n·加强对Python对象、内存模型和Python面向对象特性的深入理解；\\n·构建更有效的Web、CGI、互联网、网络和其他客户端/服务器架构应用程序及软件；\\n·学习如何使用Python中的Tkinter和其他工具来开发自己的GUI应用程序及软件；\\n·通过用C等语言编写扩展来提升Python应用程序的性能，或者通过使用多线程增强I/0相关的应用程序的能力；\\n·学习Python中有关数据库的API，以及如何在Python中使用各种不同的数据库系统，包括MySQL、Postgres和 SQLite。', 'Python核心编程（第二版）', '[\"CPUG\"]', '0'), ('1534620659', '1', '17', '[\"[\\u7f8e] Bill Lubanovic\"]', '编程', 'https://img3.doubanio.com/lpic/s28352586.jpg', '9787115407092', '404', null, '2016-1', '人民邮电出版社', 'Python语言及其应用', '本书介绍Python 语言的基础知识及其在各个领域的具体应用，基于最新版本3.x。书中首先介绍了Python 语言的一些必备基本知识，然后介绍了在商业、科研以及艺术领域使用Python 开发各种应用的实例。文字简洁明了，案例丰富实用，是一本难得的Python 入门手册。', 'Python语言及其应用', '[\"\\u4e01\\u5609\\u745e\", \"\\u6881\\u3000\\u6770\", \"\\u79b9\\u5e38\\u9686\"]', '0'), ('1534620659', '1', '18', '[\"\\u5df4\\u91cc\\uff08Barry.P.\\uff09\"]', '编程', 'https://img3.doubanio.com/lpic/s27262723.jpg', '9787512322233', '457', null, '2012-3-1', '中国电力出版社', 'Head First Python', '你想过可以通过一本书就学会Python吗？《Head First Python(中文版)》超越枯燥的语法和甩法手册，通过一种独特的方法教你学习这种语言。你会迅速掌握Python的基础知识，然后转向持久存储、异常处理、Web开发、SQLite、数据加工和lGoogle App Engine。你还将学习如何为Android编写移动应用，这都要归功于Python为你赋予的强大能力。本书会提供充分并且完备的学习体验，帮助你成为一名真正的Python程序员。    作者巴里觉得你的时间相当宝贵，不应当过多地花费在与新概念的纠缠之中。通过应用认知科学和学习理论的最新研究成果，《Head First Python(中文版)》可以让你投入一个需要多感官参与的学习体验，这本书采用丰富直观的形式使你的大脑真正开动起来，而不是长篇累牍地说教，让你昏昏欲睡。', 'Head First Python（中文版）', '[\"\\u6797\\u742a \\u7b49\"]', '0'), ('1534620659', '1', '19', '[\"\\u5e03\\u96f7\\u7279\\u00b7\\u65af\\u62c9\\u7279\\u91d1\\uff08Brett Slatkin\\uff09\"]', '编程', 'https://img3.doubanio.com/lpic/s28384052.jpg', '9787111523550', null, null, '2016-1', '机械工业出版社', '编写高质量Python代码的59个有效方法', '用Python编写程序，是相当容易的，所以这门语言非常流行。但若想掌握Python所特有的优势、魅力和表达能力，则相当困难，而且语言中还有很多隐藏的陷阱，容易令开发者犯错。\\n本书可以帮你掌握真正的Pythonic编程方式，令你能够完全发挥出Python语言的强大功能，并写出健壮而高效的代码。Scott Meyers在畅销书《Effective C++》中开创了一种以使用场景为主导的精练教学方式，本书作者Brett Slatkin就以这种方式汇聚了59条优秀的实践原则、开发技巧和便捷方案，并以实用的代码范例来解释它们。\\nSlatkin根据自己在Google公司多年开发Python基础架构所积累的经验，揭示Python语言中一些鲜为人知的微妙特性，并给出了能够改善代码功能及运行效率的习惯用法。通过本书，你能够了解到解决关键编程任务所用的最佳方式，并学会编写易于理解、便于维护且利于改进的代码。\\n本书核心内容包括：\\n涵盖Python 3.x及Python 2.x主要应用领域的实用指南，以及与之配套的详细解释及代码范例。\\n与函数相关的编程建议，这些建议有助于我们写出意图清晰、便于复用且缺陷较少的函数。\\n如何准确地表达类与对象的行为。\\n在使用元类和动态属性时，如何避免错误的用法。\\n更为高效的并发及并行方式。\\n与Python内置模块相关的编程技巧和习惯用法。\\n多人协作时所用的开发工具和最佳实践方式。\\n旨在改善代码质量和程序性能的调试、测试与优化方案。', 'Effective Python', '[\"\\u7231\\u98de\\u7fd4\"]', '0'), ('1534620659', '1', '20', '[\"[\\u7f8e] Miguel Grinberg\"]', '编程', 'https://img3.doubanio.com/lpic/s27906700.jpg', '9787115373991', '224', null, '2014-12', '人民邮电出版社', '', '本书不仅适合初级Web开发人员学习阅读，更是Python程序员用来学习高级Web开发技术的优秀参考书。\\n•	学习Flask应用的基本结构，编写示例应用；\\n•	使用必备的组件，包括模板、数据库、Web表单和电子邮件支持；\\n•	使用包和模块构建可伸缩的大型应用；\\n•	实现用户认证、角色和个人资料；\\n•	在博客网站中重用模板、分页显示列表以及使用富文本；\\n•	使用基于Flask的REST式API，在智能手机、平板电脑和其他第三方客户端上实现可用功能；\\n•	学习运行单元测试以及提升性能；\\n•	将Web应用部署到生产服务器。', 'Flask Web开发：基于Python的Web应用开发实战', '[\"\\u5b89\\u9053\"]', '0'), ('1534620659', '1', '21', '[\"[\\u7f8e]\\u7c73\\u5207\\u5c14\"]', '编程', 'https://img1.doubanio.com/lpic/s29086659.jpg', '9787115416292', '200', null, '2016-3-1', '人民邮电出版社', '', '本书采用简洁强大的Python语言，介绍了网络数据采集，并为采集新式网络中的各种数据类型提供了全面的指导。第一部分重点介绍网络数据采集的基本原理：如何用Python从网络服务器请求信息，如何对服务器的响应进行基本处理，以及如何以自动化手段与网站进行交互。第二部分介绍如何用网络爬虫测试网站，自动化处理，以及如何通过更多的方式接入网络。', 'Python网络数据采集', '[\"\\u9676\\u4fca\\u6770\", \"\\u9648\\u5c0f\\u8389\"]', '0'), ('1534620659', '1', '22', '[\"\\u8463\\u4f1f\\u660e\"]', '编程', 'https://img1.doubanio.com/lpic/s28994368.jpg', '9787121297335', '504', null, '2016-9-15', '电子工业出版社', '', '这本书涵盖了Web开发的方方面面，可以分为如下部分：\\n1. 使用最新的Flask 0.11进行Web开发，学习Jinja2、Flask-Script、Flask-Migrate、Flask-Security、Flask-Assets等8种常用扩展，帮助读者理解Flask 的 优秀设计（上下文、BluePrint等），最后利用Mako、Flask_mako、SQLAlchemy、Pillow等技术实现一个豆瓣一个真实的服务。\\n2. 阐述笔者对REST的理解，并提出一些设计API的注意事项，最后通过jQuery和fetch实现使用Ajax的例子，让读者了解如何让前后端通信。\\n3. 对Python应用服务器，Web服务器、缓存系统、键值对数据库等技术的选型和使用方法，最后演示大型网站架构及其重要组件的用意。\\n4. 使用Fabric、SaltStack、Ansible、Supervisor、Graphite等做系统管理，并演示一个通过最新的Sentry 8演示如何收集应用错误信息。\\n5. 测试和持续集成，最后使用最新的Buildbot 0.9实现一个Github项目的持续集成。\\n6. 深入RabbitMQ和Celery的原理和使用方法，最后分享笔者使用的进阶实践。\\n7. 服务化及豆瓣服务化实践。\\n8. 详细讲解豆瓣工程师都在用的DPark，包含安装、环境配置、使用和框架化分析uv&pv，接着将展示几个笔者实际工作中的数据报表需求，并讲解如何用Pandas做数据可视化。\\n9. 深入IPython和Jupyter Notebook这两个工具，并分享在豆瓣对应的实践。\\n10. 从获取Linux服务器的相关情况、性能测试、分析Python程序性能瓶颈三个方面展示对应的工具及使用方法。\\n11. 以抓取微信公众号文章为主线，分别使用多线程、多进程、Gevent、Future和asyncio这5种编程方式完成不同阶段的爬取任务，也深入地分析在它们之间如何选择。\\n12. Python进阶和Web项目经验。\\n封底推荐语\\n伟明把他个人多年 Web 开发的经验，以及豆瓣十年来数百名优秀工程师在 Web 开发上最佳实践的积累，凝聚在了《Python Web 开发实战》这本书里，多维度、全面地介绍了 Python Web 开发涉及的各种技术。我向所有有兴趣使用 Python 做 Web 开发的开发者们，强烈推荐此书。\\n——洪强宁，爱因互动CTO，前豆瓣首席架构师，前宜信大数据创新中心首席架构师\\n感谢伟明把豆瓣的一些工程实践进行了整理和总结，这是本书最宝贵的一点，库谁都会用，但在什么场景使用，在生产环境中这个库的表现到底如何，是这本书的一个精华，期望将来还可以看到越来越多这样的图书，祝此书大卖。\\n——清风，SAY CEO，前豆瓣技术总监。\\n伟明将自身在 Web 领域所有方面的经验提炼后整理成书，本质上是将几十个关联产品的官方文档，结合具体工程经验进行了梳理，给出了领域问题最佳方案的关键思考点和自己的答案，而更加可贵的是，给出了这些思考点的来源，以及形成过程，即给出了解决各种 Web 领域问题的思维模式。\\n——Zoom.Quiet（大妈），优视眼动科技 CTO，Python 中文社区创始人之一和管理员\\n阅读本书，我最大的感受就是：全和新。本书的内容涉及了Web框架、Ajax的前后端交互、测试、数据库、数据分析、服务化、部署、系统管理、常用工具等内容，有点百科全书的意味。另外，书中讲的许多东西都是现在正在流行的技术或工具，像Flask、Celery、Jupyter、Supervisor、SaltStack、Pandas等。\\n——李迎辉，Python开源资深行者，Python-CN邮件列表创建人，UliPad和Uliweb作者\\n本书由Python开发开始，循序渐进，把网站工程的全貌展现在读者的眼前，是了解Web工程从开发到上线完整流程的绝佳参考书籍。\\n——邢犇（CNBorn）, 前豆瓣东西技术负责人\\n很多刚进入Python世界的人，伟明的这本书提供了一个非常好的“知识地图”，书中涉及了Python Web开发的方方面面。\\n——胡阳（the5fire），手机搜狐网，任资深开发工程师\\n这本书从开发环境的搭建，Web 框架的使用，到最后的持续集成和 Python 的进阶用法，无一不是他多年的实际工程经验总结，十分宝贵。\\n——姚钢强（acmerfight），知乎工程师\\n这本书非常全面地介绍了使用 Python 进行 Web 开发的方方面面，既有 Web 框架、缓存、消息队列、并发处理的场景介绍和技术选型，又有开发流程、质量保证的丰富实战经验。\\n——蔡斌（VeryCB），DeepDevelop工程师，前豆瓣条目组技术负责人\\n整本书都是作者对实际Web项目中大量实战经验的总结，绝非纸上谈兵。相信通过阅读该书可以帮助开发者规避掉大量项目中的“坑”，构建出更高性能、更稳定的Web项目。\\n——Spawnris, 腾讯工程师', 'Python Web开发实战', '[]', '0'), ('1534620684', '1', '23', '[\"Vofan\"]', '港台', 'https://img1.doubanio.com/lpic/s2139837.jpg', '9789572956038', '94', null, '20060214', '全力', '', '◎收錄連載在2004年7月號～2005年7月號《挑戰者月刊》上的『COLORFUL DREAMS』。\\n作品內容以日常生活中的情境為主軸，描繪夢與現實的交界、日常生活中的非日常現象。從九份的咖啡館到八里的河口，柑仔店的小貓咪跟露天電影的回憶，天花板傳來的彈珠聲…每個色塊都像是觸動你我心弦的輕聲呢喃。\\n在《挑戰者月刊》上連載的彩色詩畫作品『COLORFUL DREAMS』，集結成冊上市！一直以來，台灣的漫畫市場出版蓬勃，而彩稿畫集的問世也不在少數。VOFAN在『挑戰者月刊』上連載一年半後，終於將推出個人首部詩畫集。有別於單純編收個人彩稿作品的其他畫集，VOFAN的每個小單元都是一則美麗動人的故事，要帶讀者進入一個奇特幻想的空間。保證看過的人都會被其神秘的魅力吸引住！\\n詩畫集中除了收錄『COLORFUL DREAMS』作品之外，還有VOFAN重新編繪的扉頁及照片介紹。加上幕後花絮、VOFAN深入訪談，絕對超值！\\n■本書目錄\\n（1） 午後三點的魔法\\n（2） 九份的咖啡店\\n（3） 我和我的守護神\\n（4） 廢墟流浪\\n（5） 城市的盡頭\\n（6） X mas Eve\\n（7） 避雨記\\n（8） 天花板的彈珠聲\\n（9） 怪談公車\\n（10） 抓迷藏\\n（11） 廟前的露天電影院\\n（12） 個人風\\n（13） 夏初飄雪的防火巷', 'Colorful dreams', '[]', '0'), ('1534620697', '1', '24', '[\"\\u62c9\\u4f5b\"]', '编程', 'https://img3.doubanio.com/lpic/s9853525.jpg', '9787508319117', '560', null, '2004-02-01', '中国电力出版社', '', '《Java数据结构和算法》(第2版)以一种易懂的方式教授如何安排和操纵数据的问题，其中不乏一些难题：了解这些知识以期使计算机的应用获得最好的表现。不管使用何种语言或平台，掌握了数据结构和算法将改进程序的质量和性能。 《Java数据结构和算法》(第2版)提供了一套独创的可视讨论专题用以阐明主要的论题：它使用Java语言说明重要的概念，而避免了C/C++语言的复杂性，以便集中精力论述数据结构和算法。经验丰富的作者RorbertLafore先生提供了许多简单明了的例子，避免了对于这类例题常见的冗长、繁锁的数学证明。在第二版中，他利用Java语言最新特性，修改并扩充了他的例子。在每一章后都有问题和练习，使读者有机会测试自己的理解程度。', 'Java数据结构和算法', '[\"\\u8ba1\\u6653\\u4e91\"]', '0'), ('1534620697', '1', '25', '[\"[\\u7f8e]\\u96f7\\u8499\\u5fb7\\u00b7\\u5361\\u4f5b\"]', '小说', 'https://img3.doubanio.com/lpic/s4145144.jpg', '9787544710442', '185', null, '2010-1', '译林出版社', '', '《当我们谈论爱情时我们在谈论什么》出版于1981年，是雷蒙德•卡佛的成名之作，同时也是其最负盛名的代表作。本书由十七篇短篇小说组成，讲述了如餐馆女招待、锯木厂工人、修车工、推销员和汽车旅馆管理员等社会底层的体力劳动者的生活。这些普通人有着普通人的愿望，做着再普通不过的事情，但他们发现自己在为生存而挣扎，无法获得在常人看来并不远大的人生目标。他们的生活中充满了窘困和不如意，婚姻破裂，失业，酗酒，破产。卡佛用“极简”的遣词、冷静疏离的叙事，表现了现代社会中人的边缘性以及现代人脆弱的自我意识。', '当我们谈论爱情时我们在谈论什么', '[\"\\u5c0f\\u4e8c\"]', '0'), ('1534620697', '1', '26', '[\"[\\u7f8e]\\u96f7\\u8499\\u5fb7\\u00b7\\u5361\\u4f5b\"]', '小说', 'https://img1.doubanio.com/lpic/s3405687.jpg', '9787544706001', '245', null, '2009-1', '译林出版社', '', '几乎所有评论家都从这本《大教堂》中嗅出卡佛的变化来。在这本创作于1982年到1983年间的小说集中，如果说人物和情节仍延续了卡佛早期作品的灰暗和无法自拔的话，在几篇篇幅较长的作品的结尾，我们可以看到卡佛把自己那扇一直尘封的天窗推开一条缝隙，洒下了一点点光亮。我们仍然可以读到一系列小人物在生活的泥潭里摸爬滚打：重逢恋人被轰出了刚刚借来的房间（《瑟夫的房间》）；生活就像坏掉的冰箱里的食物一样腐臭变质（《保鲜》）；奔波的人失望地离开，扔下了曾经挚爱的东西（《马笼头》）；父亲在痛苦的回忆中，拒绝下火车去见来接站的儿子（《软座包厢》）；甚至因耳屎堵住了耳朵而带来的不便和恐惧（《小心》）。交流的匮乏和艰难，成了整部小说集凸显的主题，不管是在父子间、情人间、夫妇间，还是朋友间，甚至萍水相逢的人之间，语言和身体的交流成了所有灰暗中最苍白的角落。但在《好事一小件》、《电话来自何方》以及《大教堂》中，卡佛正在步履维艰地一点点“远离那威胁性的模糊，朝着希望而不是恐怖迈进” ，不管这种希望是多么的微弱和渺茫。我们读到了一对刚刚失去儿子的夫妇和一个一直打来骚扰电话的面包师之间的和解；一个酒鬼鼓起勇气，准备拨通妻子的电话；一个男人放下了心理的抵触，在一个盲人的指触下，闭上眼感觉着大教堂。在访谈中，卡佛解释了自己的改变：“我想，现在的感觉更加乐观了一点。当然在大部分小说中，人物的麻烦得不到解决。人们的目标和希望枯萎了。但有时，而且恐怕是经常，人们自己不会枯萎，他们把塌下去的袜子拉起来，继续走。”', '大教堂', '[\"\\u8096\\u94c1\"]', '0'), ('1534620697', '1', '27', '[\"[\\u7f8e]\\u96f7\\u8499\\u5fb7\\u00b7\\u5361\\u4f5b\"]', '小说', 'https://img3.doubanio.com/lpic/s3958190.jpg', '9787020075089', '432', null, '2009-9', '人民文学出版社', '', '卡佛一生作品以短篇小说和诗为主。这本自选集几乎包括了卡佛所有最重要的短篇小说，被誉为是“最卡佛”的卡佛小说集。本书总结了卡佛的写作生涯，见证了他堪比海明威、韦尔蒂、塞林格、契佛等当代美国名家的文学成就……这部自选集是来自内心最深处的声音。', '雷蒙德·卡佛短篇小说自选集', '[\"\\u6c64\\u4f1f\"]', '0'), ('1534620697', '1', '28', '[\"[\\u7f8e]\\u6cd5\\u5170\\u514b\\u00b7\\u4f5b\\u675c\\u9521\\u514b\"]', '小说', 'https://img1.doubanio.com/lpic/s3063977.jpg', '9787535745675', '244', null, '2006-5', '湖南科学技术出版社', '', '本书记述了一名钢铁工人蜕化为具有人道关怀思想的神经外科医师的里程碑。作者以一个黑色幽默的角度看待神经外科医生的修炼历程，描绘医院里为生死博斗的悲喜剧。行文鲜活生动、风趣幽默，却又带着令人鼻酸的感伤与无奈。故事情节紧凑、明快，在时而欢笑、时而垂泪的阅读中，深刻体验医生工作的甘苦、压力与使命，病患流露出的无助、期望与感激。文中提到内科与外科的互相排斥，医生面对病患终告不治时的悲哀与痛切，医生与护士或其他医护工作人员的小小冲突磨擦等等，也是医界不断上演的戏码，不分国界。我相信，读者阅读起来，有如聆听医生的肺腑之言，文中情境也都可能发生在自己身上。在药水、针筒、电子仪器的场景中，交织成有血有泪，却又真实无比的人生，让人体悟更深、感受更强、启示更鲜明。\\n要知道这是官方简介。\\n一个从医大毕业的实习医生转变了医院的所有科，终于选择了神经外科。并且在那个拥有“疼痛博物馆”的科室，一待就是七年。', '神经外科的黑色喜剧', '[\"\\u5434\\u7a0b\\u8fdc\"]', '0'), ('1534620697', '1', '29', '[\"[\\u7f8e] \\u845b\\u745e\\u683c\\u00b7\\u6469\\u987f\\u68ee\", \"[\\u7f8e] \\u5927\\u536b\\u00b7\\u5965\\u5229\\u4f', '小说', 'https://img3.doubanio.com/lpic/s3474155.jpg', '9787807027157', '257', null, '2009-1', '吉林文史出版社', '', '在巴基斯坦的世界中，“三杯茶”，是巴尔蒂人交朋友的方式。\\n第一杯茶，你是陌生人；\\n第二杯茶，你是我们的宾客；\\n第三杯茶，你是我们的家人，我们愿意为你做任何事，甚至是死。\\n当摩森顿于攀登K2峰迷路，科尔飞村居民拯救了他的生命。当地的居民生活艰困，糖是如此稀少珍贵，他们却为他煮了甜茶，让他恢复力气。此后，从第一杯茶到第三杯茶，从陌生人到愿意以生命守护彼此，“三杯茶”代表的是他们之间珍贵的信任，更是一生的承诺，也是一个旅人，改变世界的开始。\\n摩顿森原来是个登山家，1993为了纪念早逝的妹妹，展开了攀登世界第二高峰K2的旅程，却发生意外，与　队友们失去联络，只留下他一人，没有水也没有食物。十分幸运地，他在巴基斯坦山区的一个偏僻小村落被救起，而且在村民的细心照护下，他的身体得以康复。\\n那里景色如画，看似有如香格里拉般的人间仙境，但，却不是想象中的伊甸园。村民的善良所感动的摩顿森发现，村落里有三分之一的孩子活不过一岁生日，最近的医院得走上一星期的路，孩子们最大的愿望，是一间有屋顶的学校。在寒风里，当地小孩只能跪在户外霜冻的土地上，或坐在脏污的泥地上上课。村民穷到没钱聘请老师。摩顿森于是承诺，他一定会回来为他们办所学校……\\n在过去的十二年，巴基斯坦、阿富汗及西藏山区，六十所学校陆续成立，最特别的是，这些学校让原本无法上学的女孩开始接受教育。摩顿森守护妹妹的信念，改变了一群女孩的生命，是她们不再藏匿于面纱之后，有勇气面对世界，甚至拥有改变世界的能力。\\n当我们怀疑，一个人的力量是否真的能改变世界时，摩顿森做到了。为了坚持理想，即使受到恋人的遗弃、社会的漠视、巴提人的拐骗，即使与妻儿分隔两地、被军阀囚禁，即使遭逢美国911事件、美国轰炸阿富汗，在悲伤、沮丧、孤独与滨临死亡之时，他从来没有遗忘他的承诺，他愿意用生命去化解世界上最大的仇恨。', '三杯茶', '[\"\\u9ec4\\u7389\\u534e\", \"\\u4e25\\u51ac\\u51ac\"]', '0'), ('1534620697', '1', '30', '[\"[\\u7f8e] \\u5c3c\\u5c14\\u00b7\\u57c3\\u4e9a\\u5c14\", \"[\\u7f8e] \\u745e\\u5b89\\u00b7\\u80e1\\u4f5b\"]', '互联网', 'https://img3.doubanio.com/lpic/s29436163.jpg', '9787508668314', '224', null, '2017-5', '中信出版集团', '让用户养成使用习惯的四大产品逻辑', '——为什么我们会习惯性地点开某个App？\\n——这种使用习惯到底是如何养成的？\\n——为什么有些产品能让我们戒不掉，而其他的产品却不行？\\n——是否有什么秘诀能让用户对你的产品形成使用习惯，欲罢不能？\\n《上瘾》揭示了很多让用户形成使用习惯，甚至“上瘾”的互联网产品服务背后的基 本设计原理，告诉你怎样打造一款让用户欲罢不能的产品。作者根据自己多年的研究、咨询及实际经验，提出了新颖而实用的“上瘾模型”（Hook Model），即通过四个方面来养成用户的使用习惯。通过连续的“上瘾循环”，让用户成为“回头客”，进而实现循环消费的终极目标，而不是依赖高昂的广告投入或泛滥粗暴的信息传播。\\n本书专为产品经理、设计师、市场销售人员、广告创意者打造，每一位跟用户行为打交道的人，都值得将其奉为案头必备指南。对于想要了解行为设计科学的读者来说，这本书也将让你对自己、对当前的热门产品有更深刻的认识。你将会看到：\\n“上瘾”产品中暗藏的用户习惯养成关键\\n打造一款用户习惯养成类产品的实操步骤\\nFacebook、Twitter、Instagram、Pinterest等国际知名互联网产品常用的用户行为设计解析', '上瘾', '[\"\\u949f\\u8389\\u5a77\", \"\\u6768\\u6653\\u7ea2\"]', '0'), ('1534620697', '1', '31', '[\"\\u5f20 \\u8389\", \"\\u5e03\\u9c81\\u65af\\u30fb\\u4f5b\\u683c\", \"\\u6613\\u5c0f\\u7433\", \"\\u5b97\\u4f1a\\u6765\"', '科普', 'https://img1.doubanio.com/lpic/s1337848.jpg', '9787109061507', '128', null, '2000-3', '中国农业出版社', '', '《养猫指南》采用大信息量的灌输方式，来改变您对自己猫咪的看法。宠物权威专家，布鲁斯·佛格博士，在其担任兽医时曾拥有数百只猫咪。他从多方面就猫咪的习性进行讲解，准确分析它正在做什么（通过观察猫咪的形体语言和常常非常滑稽的动作），然后解释为什么这样做。对许多养猫者来说，猫咪的重要性可与人相比。《养猫指南》用一整章考证了猫咪与养猫者之间的关系，猫咪是如何看待主人的，以及它与其他猫咪伙伴和动物戏耍的习性。另一章节通过观察猫咪每天的活动，使读者明白如何为健康和幸福的猫咪创造最好的环境。\\n专门摄制的图片将包罗各种形态的猫咪，如此细腻表现猫咪的图片会使您感到惊讶。通过观察日常情况下猫咪的各种行为举止，能使您对小猫咪从出生到长大的每个阶段都非常熟悉，甚至到了不可思议的程度。\\n您已与猫咪同住，读过《养猫指南》后您还能知道猫咪正在想什么！', '养猫指南', '[\"\\u6613\\u5c0f\\u7433\"]', '0'), ('1534620697', '1', '32', '[\"\\u5361\\u841d\\u5c14\\u00b7\\u97e6\\u5fb7\", \"\\u5361\\u841d\\u5c14\\u00b7\\u5854\\u4f5b\\u745e\\u65af\"]', '科普', 'https://img3.doubanio.com/lpic/s2716016.jpg', '9787301095423', '656', null, '2006-09', '北京大学出版社', '', '不懂心理学的人，很难算是一个真正的人。心理学是一门最贴近我们每个人自身的学科，它的研究内容无所不包，从先天的基因遗传到后天的环境改变，涉及人的所有；它的研究跨度更是无所不容，从胎儿期到老年期，横贯人的一生，可谓是与人的毕生发展息息相关。\\n本书按照个人成长经历的六个方面——自我、身体、心理、环境、心理健康、生活——通过独特的组织方式、引人人胜的生动叙述、给人启迪的鲜活案例，展示了心理科学的方方面面，给读者提供了一个思考人类行为的构架，带领大家积极探索心理学研究领域，深入了解我们的身体与心灵和我们所置身的这个世界。\\n本书易读、易学、易入，科学性、严谨性、趣味性兼容，尤其注重培养你的批判性和科学思维，这也是本书有别于同类书籍的最大特点。本书适用于寻求心理学入门的人、有兴趣深入探索自身内在工作秘密的人。如果你想认识自己，了解他人，实现自我，就请接受我们的邀请，进人心理学为你开启的这扇大门。', '心理学的邀请', '[\"\\u767d\\u5b66\\u519b\\u7b49\"]', '0'), ('1534620697', '1', '33', '[\"\\u5357\\u6000\\u747e\"]', '科学', 'https://img3.doubanio.com/lpic/s1052304.jpg', '9787309031935', '232', null, '2002-6', '复旦大学出版社', '', '《华严经普贤行愿品》原是唐代般若译的《华严经》四下卷中的最后一卷，因其文理俱优，切于日用，译后不久，便函独成一经，广为流传。经中主要讲述了普贤菩萨的十大行愿，佛教寺院中每日引磬唱诵的《普贤菩萨十大愿》即源于此。\\n这本《学佛者的基本信念》书是南怀瑾有关《普贤行愿品》的讲记。作者以通俗的口语，对该经的趣大意，特别是一般人不易读懂的偈颂作了别开生面的讲解，并附列了有关“行愿”方面的各种资料。书名中“基本信念”即是指“行愿”而言的。', '学佛者的基本信念', '[]', '0'), ('1534620697', '1', '34', '[\"\\u9648\\u5175\"]', '心理学', 'https://img3.doubanio.com/lpic/s3562725.jpg', '9789574572076', null, null, '2007.09.01', '佛光出版社', '', '本書從現代心理學和佛法的雙重視角，對散見於大量佛典中的各時期、各地區、各宗派有關心識的思想作了一番現代整合，按其所涉及的重要內容和其間的邏輯聯繫分章，力圖忠實地概括全體佛教心理學思想的原旨，並作出使現代人比較容易明白的詮釋。不囿於佛教某一宗派之學，而整合從古到今的全體佛法。引現代心理學證佛學、補充佛學；引佛學補充、昇華現代心理學，並以心性及明心見性的方法?精髓，是本書的特點。對古代佛教著述中較?薄弱而從現代心理學看來顯得重要的問題，則儘量依據佛教義理進行必要的補充發揮。在論述、引證佛典時，儘量按歷史順序安排先後，以反映佛教思想演進的路線，不違學術的科學性。 作者陳兵教授從開始研究到完成書稿，花了11年時間，全書約80萬餘字，分為上、中、下三冊，是一本佛教心理學的教材和普及讀物，提供佛教徒修行的輔導，心理學工作者的重要參考資料，也是廣大愛好佛學和心理學讀者了解佛教心理學和佛法的方便法門。', '佛教心理学', '[]', '0'), ('1534620698', '1', '35', '[\"[\\u65e5]\\u6cb3\\u5408\\u96bc\\u96c4\"]', '心理学', 'https://img3.doubanio.com/lpic/s1361143.jpg', '9789867574275', null, null, '2004年10月14日', '心靈工坊', '', '當河合隼雄1965年從瑞士學成歸國，成為日本第一位容格心理分析師時，他並不知道自己日後會深深迷戀佛教。從幼年起，他就對佛教抱著排斥的態度，因為童年時弟弟夭折、母親悲傷的畫面，跟誦經儀式一起深植腦海，讓他意識到自己內心對死亡的不安與焦慮。河合隼雄十七歲時，日本戰敗，他開始崇拜西方的理性主義和科學，認為這是使日本從失敗中站起來的唯一道路。1959年他到美國研讀心理學，一位心理分析師向他展示禪宗的「十牛圖」，他很慚愧，因為他身為東方人，卻對它一無所知。後來，他來到蘇黎世繼續心理分析師的訓練，展開夢的分析歷程，才驚覺日本古典文學、童話、神話傳說對他具有重大意義。在傳統文化中，他終於找回自己的根。回到日本後，當時社會還無法接受心理分析的觀念，河合隼雄只好從沙遊治療做起。在治療個案的過程中，他發現佛教文化已深植在日本人的潛意識中，而他自己也漸漸對佛教豐富的象徵意涵著迷。他對佛教了解越多，越覺得佛教思想對他的治療工作幫助很大。到後來，他甚至告訴西方學者：「你們西方人講個人心理治療、人際心理治療、超個人心理治療－我則講無我（no-ego）心理治療。」他的目標是幫助個案變得像石頭一樣，在天地之間安住自在。《佛教與心理治療藝術》就是從河合隼雄自己的心路歷程談起，他自身就是一則公案：「我是誰？我是一個佛教徒？還是心理分析師？」他由西方心理學訓練中繞一個大圈子，才重新發現東方心靈的本質。接著，河合隼雄以禪宗「十牛圖」和容格的「玫瑰園圖」做比較，探討東方的自性成長歷程和西方的心靈煉金術，兩者間的相似與共鳴，既具藝術價值又有哲學深度。到底「我」是什麼？身為東方的心理治療師，河合隼雄提出「靜觀無為」的方法，認為內心深處要保持沈默，將個案的抱怨和症狀當作禪宗公案，將自己放在悲憫的中央位置，不是告訴個案這個世界有多美好，而是以溫柔的悲憫和關愛，和個案共同承擔生命的悲傷與重擔。悲憫久了，真正的喜悅將會降臨。這才是人和人之間相互治療的真正基礎。河合隼雄吹得一首好笛，有一次他在美國演講期間，成群鳥兒棲落在他窗前的樹枝上，傾聽他的笛聲。天地萬物，融為一體，這正是他對人類心靈本質及潛能最深刻的體悟吧！', '佛教與心理治療藝術', '[\"\\u912d\\u798f\\u660e.\"]', '0'), ('1534620698', '1', '36', '[\"\\u5415\\u6f82\"]', '历史', 'https://img1.doubanio.com/lpic/s1604198.jpg', '9787208055117', '315', null, '2005-05', '上海人民出版社', '', '本书是作者受原中国科学院哲学社会科学部的委托，在1961年开办为期五年的佛学班上所授用的讲义稿。印度的佛学思想开始流行，是在公元前5世纪，以后逐渐扩展发达起来，一直到公元10世纪大乘佛学在印度衰微，前后经历了一千五百年。在这漫长的时期里，印度佛学的学说本身也经历了好几次大的变化：由原始佛学到新派佛学，再后又发展成大乘和小乘，大乘本身也还分初、中、晚期之分，这是很明显的几个阶段。本稿将印度佛学分为原始佛学、新派佛学，初期大乘佛学、小乘佛学、中期大乘佛学和晚期大乘佛学六个阶段，根据汉文、藏文的大量文献，对勘巴利文三藏以及现存的有关梵文原典，按照各阶段出现的典程生后顺序，说明它们各时期学说的特点和变化，对印度佛学一千五年的历史发展概况，基本上勾画出了一个清晰的轮廓。据整理者称，这是我国第一部原原本本讲述印度佛学史的书藉。', '印度佛学源流略讲', '[]', '0'), ('1534620698', '1', '37', '[\"\\u5415\\u6f82\"]', '历史', 'https://img1.doubanio.com/lpic/s10048618.jpg', '9787101004113', '396', null, '2004-05', '中华书局', '', '中国佛学是随着由印度传来的佛教而产生的一种宗教哲学。佛教传入中国后和中国原有的思想相接触，不断变化，不断发展，最后形成了自己的特殊的新学说。在其发展变化过程中，一方面，印度发展着的佛教思想仍在不断传来，给予了它持续的影响，另方面，已经形成的 中国佛学思想也逐步成熟，构成了如天台、贤首、禅宗等各个体系。', '中国佛学源流略讲', '[]', '0'), ('1534620698', '1', '38', '[\"\\u7f57\\u4f2f\\u7279\\u00b7\\u6bd4\\u5c14\"]', '历史', 'https://img3.doubanio.com/lpic/s4540270.jpg', '9787800578625', '274', null, '2007-4', '中国藏学出版社', '', '本書介紹藏傳佛教裏豐饒、多元的象徵符號與器物圖像，深入探討其源頭、意義與功能，囊括各式吉祥物、動物與神話動物、密宗器具、兵器、神奇、植物、供物、手印等，以白描的畫法、簡潔之中不減繽紛。全書共分十六章、四個附錄、詞彙解釋及兩百餘幅插圖，並由譯者加註千餘條註釋，方便讀者探索這些器物、圖像深邃的宗教内涵之餘，也增添了學術上的價值。不論就宗教與藝術的題材，還是圖像與文字的搭配，本書都稱得上是獨一無二的著作。對藏傳佛教文化、藝術、象徵符號有興趣的讀者，本書絕對值得珍藏；對於一般藝術、文化工作者也有不可磨滅的參考價值。', '藏传佛教象征符号与器物图解', '[\"\\u5411\\u7ea2\\u7b33\"]', '0'), ('1534620698', '1', '39', '[\"[\\u52a0\\u62ff\\u5927]\\u535c\\u6b63\\u6c11\"]', '历史', 'https://img1.doubanio.com/lpic/s29106957.jpg', '9787508665450', '2264', null, '2016-10', '中信出版社', '', '【内容简介】\\n哈佛中国史（全六卷）\\n[加]卜正民（Timothy Brook）主编\\n01：早期中华帝国：秦与汉\\n[美]陆威仪（Mark Edward Lewis）著　王兴亮　译\\n02：分裂的帝国：南北朝\\n[美]陆威仪（Mark Edward Lewis）著　李磊 译　周媛 校\\n03：世界性的帝国：唐朝\\n[美]陆威仪（Mark Edward Lewis）著　张晓东 冯世明 译　方宇 校\\n04：儒家统治的时代：宋的转型\\n[德] 迪特•库恩（Dieter Kuhn）著　李文锋 译　邵君安 校\\n05：挣扎的帝国：元与明\\n[加]卜正民（Timothy Brook）著　潘玮琳　译\\n06：最后的中华帝国：大清\\n[美]罗威廉（William T. Rowe）著　李仁渊 张远　译\\n----------\\n《哈佛中国史》六卷本丛书，由国际著名汉学家卜正民教授领衔主编，集结罗威廉、陆威仪和迪特•库恩三位知名汉学家，萃集半个世纪以来西方最新的中国史研究成果，以全球史视野、多学科学识颠覆传统中国史叙述模式，倾十年之功写就，是继《剑桥中国史》之后最能代表西方半个世纪以来中国史研究全新成果和水准的多卷本中国通史。\\n《哈佛中国史》丛书站在风起云涌的21世纪，从全球史角度重写“世界中国史”，引导世界重新思考当下中国。丛书上自公元前221年秦朝一统天下，下至20世纪初清朝终结，分为六个帝国时代——秦汉古典时代、南北朝大分裂、世界性帝国唐朝、宋朝的社会转型、气候变迁影响下元明帝国的兴衰，以及成就斐然、盛况空前的大清王朝，进而串起2000年中华文明跌宕起伏的荣辱命运。\\n《哈佛中国史》丛书专为普通读者而作，语言生动活泼，文风简明精悍，结构精致合理，极富故事性和启发性，是一套给大众读者的权威、简洁、清晰、独特的全新中国通史。\\n《哈佛中国史》丛书出版后获得很多赞誉，被称为“多卷本中国史的黄金标准”，堪称哈佛大学出版社的典范之作，已被芝加哥大学、康奈尔大学、不列颠哥伦比亚大学、香港科技大学、香港城市大学等数十所世界知名大学指定为中国史课程教材。\\n------------------------------\\n【编辑推荐】\\n【哈佛大学出版社典范之作，数十所世界一流高校中国史课程指定教材】\\n哈佛大学出版社先后于2009—2013年出版六卷本《哈佛中国史》丛书，面向大中学生和普通读者，汲取50年来世界中国史最新研究成果，谱写21世纪全新中国通史。\\n2009年出版以来，《纽约时报》《泰晤士报》《美国历史评论》《亚洲研究》《伦敦时报》等近百家媒体争相报导，好评如潮；哈佛大学教授包弼德、史乐民，剑桥大学教授麦大维，牛津大学教授戴彼得，华盛顿大学教授伊沛霞等世界知名汉学家一致推荐；越来越多的高校将其指定为中国历史课程的教科书或参考书，如康奈尔大学、芝加哥大学、宾夕法尼亚大学、莱顿大学、不列颠哥伦比亚大学、华盛顿大学、香港科技大学、香港城市大学等，备受欧美大中学生和普通读者的青睐。\\n【代表50年来世界中国史研究的全新成果，多卷本中国史的黄金标准】\\n《哈佛中国史》是继《剑桥中国史》之后反映50年来世界中国史全新研究成果、代表世界中国史研究较高水准的21世纪全新中国通史。与皇皇十余卷、内容专深、多位作者合力撰写一卷的《剑桥中国史》不同，《哈佛中国史》每个断代均由一位学者一气呵成，文风通贯，浑然一体。\\n《哈佛中国史》以全球视野突破传统中国史学叙事，真正做到了跨学科、跨领域讲述中国历史，全面涉及天文学、地理学、考古学、人类学、社会学、气候学、环境史、医疗史、民族史、中西交流史等多个学科的新角度、新方法和新成果，堪称21世纪史学研究的新典范。\\n《哈佛中国史》丛书共六卷，分秦汉、南北朝、唐、宋、元明和清六个时代分野，上迄秦朝统一，下至清朝终结，以一种新鲜、紧凑、好读的方式涵盖了公元前2世纪至20世纪初期中国古代王朝兴起、发展和衰亡的历史进程。通贯上下，分量适中，权威详尽，获赞“多卷本中国史的黄金标准”。\\n【4位国际顶尖汉学家，倾10年之功】\\n《哈佛中国史》丛书由国际顶尖汉学名家卜正民领衔罗威廉、陆威仪、迪特•库恩，倾10年之功辛勤创作，集世界中国史研究之大成。卜正民擅长以新颖的视角、丰富的细节和极具感染力的叙事风格讲述元明史；罗威廉为驰名国际的清史研究名家，所作“清卷”被誉为数十年来海外清史研究必读的通俗佳作；陆威仪为斯坦福大学李国鼎中华文化讲座教授，著名中国古代史专家，凭一己之力独立完成秦汉、南北朝和唐朝三卷；迪特•库恩专长于宋代物质文化史、科技史和艺术史，是德国重要的宋史研究专家，在世界汉学界享有盛名。\\n【权威、简洁、清晰、独特的全新中国通史，人人爱读的大众历史读物】\\n《哈佛中国史》丛书专为普通读者撰写，语言生动活泼，文风简明精悍，结构精致合理，极富故事性和启发性，是一套写给大众读者的权威、简洁、清晰、独特的全新中国通史。历史学习者可从中了解世界中国史研究前沿，开阔新视野，学习新方法、新成果，而大众读者也可在享受会心愉悦的阅读体验之余，获得对中国历史的崭新认识，面对这套“大不一样的中国史”，豁然开朗。\\n【20位中国史专家一致推荐】\\n著名历史学家葛兆光长序推荐，秦汉史专家王子今、李开元、卜宪群、彭卫，南北朝史专家罗新、楼劲，唐史专家张国刚、郝春文、刘后滨，宋史专家邓小南、李华瑞，元明史专家商传、张帆、李新峰、孙竞昊、方骏，清史专家李伯重、赵世瑜、夏明方等联袂推荐！\\n【中文版出版团队酝酿10载，编辑历时5年】\\n中文版出版团队酝酿10载，邀请海峡两岸优秀青年历史学者王兴亮、潘玮琳、李仁渊、张远、李文锋等携手翻译，编辑历时5年，精细打磨，力图向大众读者呈现一套可靠、可信、可读的中国通史。\\n--------------------------\\n【名家推荐】\\n我希望我们抱着生命经验之复杂的想法，而不是退回到“历史事件之所以发生就是因为它该发生”那一套统一的、早已建构出的历史叙述中。我们应该从自己所写时代的内部而非外部来呈现那些岁月，但同时，我们的写作也需要囊括该时期内中国所征服的地区，这就要求我们不仅从内部来观察，还要具备全局眼光，将影响该地区历史进程的非汉人形象更加具体。读者会在这一套书中见证朝代兴衰，但也能更多地了解某个时代的经济、社会、文化，以及人民的日常生活，而不是像在故事书中那样只看到皇帝和大将军们的传奇故事。\\n——《哈佛中国史》主编卜正民，中文版总序\\n透过海外中国学家对“中国/历史”的叙述，我们不仅能看到“异域之眼”中的“中国史”，而且能看到塑造“中国史”背后的理论变化，也能看到重写“中国史”背后的世界/全球史背景。其实，当他们用流行于西方的历史观念和叙述方法来重新撰写中国史的时候，另一个“世界中的中国史”就产生了。\\n——葛兆光，历史学家，复旦大学教授，《哈佛中国史》推荐序\\n六卷本“哈佛中国史”丛书成功地填补了一卷本中国史教科书和多卷本的多位作者合力撰写一卷而成的《剑桥中国史》之间的空白，为读者提供了一个基于朝代、每卷由一位作者独自撰写完成的解释性历史。\\n——包弼德，美国历史学家、哈佛大学教授\\n权威、详尽的六卷本《哈佛中国史》丛书一直被奉为多卷本中国史的黄金标准。哈佛大学出版社出版的这套六卷本中国史以一种新鲜、紧凑、易读的方式涵盖了公元前2世纪到20世纪初期中国古代王朝兴起、发展和衰亡的历史进程。\\n——独立学者查尔斯• W.海福特\\n这一精彩的由著名汉学家卜正民教授主编的《哈佛中国史》丛书，为哈佛大学出版社带来了至高的荣誉。它让我们从不同的角度思考中国。\\n——历史学家乔纳森·米尔斯基\\n《早期中华帝国：秦与汉》一书中有些见解或许可以作为对我们的考察和理解有所助益的“他山之石”。比如，作者陆威仪教授在政治史、军事史之外，以“帝国的城市”“农村社会”“外部世界”“宗族”“宗教”“文学”“法律”几个方面说明秦汉历史，这样的分析方式富有新意。中国的秦汉史学者有必要从西方学者的文化理念、学术视角、考察思路、研究方法借鉴其优异之处，通过比较鉴别，扩展自己的学术视野，提升自己的学术品质。\\n——王子今，秦汉史专家，中国秦汉史学会会长、中国人民大学教授\\n回归叙事，是历史学的新趋势，不仅中国如此，西方也是如此。叙事有种种形式，形式的变化和创新，应是历史学的追求。基于深厚的学术研究基础，作出精准的概括，写成易懂的叙事，是需要相当的努力才能达到的境界。我相信，阅读《早期中华帝国：秦与汉》一书的读者，一定会体验到作者陆威仪的苦心而受益。\\n——李开元，秦汉史专家，日本就实大学人文科学部教授\\n《早期中华帝国：秦与汉》是继《剑桥中国秦汉史》之后又一部认识和了解西方学者对秦汉历史总体看法的佳作。它把秦汉放在世界史、中国史的整体视野中观察，较为全面系统地描述了秦汉历史的概貌与演变。陆威仪教授把目光深入到了历史的内部，用自己的体验与理论，而非“早已建构出的历史叙述”体系，在各个专题中表达了自己不少独特的见解。与此同时，他对秦汉传世文献、出土文献、碑刻资料以及西方学术界的研究成果极为熟悉，信手拈来，运用得当，体现了一位专业史学家的高超素养。\\n——卜宪群，秦汉史专家，社科院历史研究所所长、研究员\\n《分裂的帝国：南北朝》讲述汉朝崩溃到隋朝统一之间四百年的历史，与中国和日本最常见的通史型著作不同的是，政治史所占篇幅有限，而且这有限的篇幅也重在讲述引起政治变动的结构性因素的发展，历史解释的思想性因素被放在优先位置。作者陆威仪教授最深的关怀是人和人的精神世界，通过描述社会与文化如何、为何发生深刻的变化，以及这些变化如何塑造那个时代的生活环境，一扫陈陈相因的旧说所型塑的刻板印象，从多个角度展示该时代独有的魅力。\\n——罗新，魏晋南北朝史专家，北京大学教授\\n《分裂的帝国：南北朝》一书对魏晋南北朝史的重写有相当坚实的理据，具备了“成一家之言”的基本条件。从历史空间的拓展，到士族、军将等历史要角的崛起和影响，再到芸芸众生的物质生产和精神活动态势，以及中外关系、社会体制、宗教形态和文化生活等领域所示特色的递嬗，陆威仪教授力图以此凸显当时一系列历史转折的内涵及其对此后中国史发展的意义。且其展现于读者面前的具体样态，又堪称精神饱满而文史贯通，加之其通篇娓娓而谈、见微知著又要言不繁，遂能令人读之甚快而又会心深思。\\n——楼劲，南北朝史专家、中国南北朝史学会会长、中国社科院历史研究所研究员\\n《世界性的帝国：唐朝》是写给西方读者阅读的一本概要性唐史著作，其谋篇布局不拘套路，抓住主要问题，不求面面俱到，有自己的独特叙事框架。有些内容，作者陆威仪教授具有自己独特的研究心得，有些内容则比较充分地综合了西方学者的研究成果，观察问题的视角独具一格，加上翻译语言和概念上的格义效应，在整体叙事上给人以耳目一新之感！作为他山之石，对于国内读者有不可多得的参考意义。本书还保留了作者引用文献的出处，对于国内同行了解西方学者的相关研究成果，也不无裨益。值得向读者诸君郑重推荐！\\n——张国刚，唐史专家，清华大学教授\\n《世界性的帝国：唐朝》通过对唐代政治、经济、军事、地理、城市、乡村、宗族、宗教、文化以及外部世界等方面的具体描绘，以生动的事例向读者展现了中国古代高峰期异彩纷呈的恢弘画卷。作者陆威仪教授着重论述了在唐代社会诸多领域陆续发生的巨大变革，凸显了唐代历史的特点，也揭示了这一时代在中国古代承前启后的历史地位，开启了帝制时代后期的历史。\\n——郝春文，唐史专家，中国敦煌吐鲁番学会会长、首都师范大学教授\\n如何向对中国古代历史几无了解的读者讲述其最辉煌的朝代，对于任何人来说都是一个很大的挑战。由师从何炳棣先生的斯坦福大学中国历史教授陆威仪执笔的《世界性的帝国：唐朝》一书无疑取得了很大的成功。他对唐代历史观测点的选择独具特色，众多小故事背后都有大关怀，从帝制中国整体特征的宏观视野提出或综括出了许多独特的切入点。即使站在一个挑刺的专业研究者角度，我对本书的阅读体验也相当愉悦。\\n——刘后滨，唐史专家，中国人民大学教授\\n内容广博、行文晓畅是《儒家统治的时代：宋的转型》的', '哈佛中国史（全六卷）', '[\"\\u738b\\u5174\\u4eae\", \"\\u674e\\u78ca\", \"\\u5f20\\u6653\\u4e1c\", \"\\u51af\\u4e16\\u660e\", \"\\u674e\\u6587\\u95', '0'), ('1534620698', '1', '40', '[\"\\u8fea\\u5e15\\u514b\\u00b7\\u9a6c\\u5c14\\u970d\\u7279\\u62c9\\uff08Deepak Malhotra\\uff09\", \"\\u9a6c\\u514b\\', '心理学', 'https://img3.doubanio.com/lpic/s3975006.jpg', '9787300109671', '239', null, '2009-9', '中国人民大学出版社', '', '为什么我们和全球铁矿石供应商的谈判屡谈屡败？为什么中石化在伊拉克的石油竞标中丢了西瓜捡了芝麻？因为我们缺少了这样一批人：\\n他们相机而动；他们善于倾听；他们大智若愚；他们不屑说谎……他们就是谈判专家。专家没有敌人，只有目标；专家眼中是谈判对方，而非谈判对手；专家要赢得的是这场谈判，而不是谈判对手；专家玩的不是“零和游戏”，专家的目的“双赢”……就在本书中，你将亲历谈判专家的与众不同，如果巴泽曼能说服你从此时此刻开始实践，那么在合上本书的时候，相信你已经从一位谈判人成长为一位谈判专家。', '哈佛经典谈判术', '[\"\\u5434\\u5955\\u4fca\"]', '0'), ('1534620698', '1', '41', '[\"\\u5434\\u548f\\u6167\"]', '杂文', 'https://img3.doubanio.com/lpic/s1106220.jpg', '9787108009845', '180', null, '1997-3-1', '三联书店', '', '《哈佛瑣記》是筆者個人對哈佛求學時代一些瑣細的回憶。除最末一篇----〈重返哈佛〉----是描寫去年歲末重返哈佛的經歷之外，其餘十四篇都是記載一九七七年至一九八三年之間的見聞。\\n不可否認的，這些回憶都十分主觀，因此不免是片面的。讀者不難在字裡間發現筆者對哈佛的偏愛，這個偏愛實源於六年哈佛生活所產生的深摯情感，而不是可以用理性來推論的。\\n每個人內心深處都隱藏一個奧秘的精神泉源，不斷地支持他在現實世界裡過活，尤其在俗事紛擾之際，還能回到自己心靈的世界滋養休息，以便重新出發。\\n哈佛的可貴之處，便是提供一個良好的氣氛，讓每個人嘗試去表達自己，發覺真實的自我。在哈佛，每人受到鼓勵用自己的想法去思考，用自己的感官去感覺，按照自己喜愛的方式去生活。除了個體層面的解放之外，哈佛還能夠承接西方智識的傳統，使置身其他的人隨時可以與古人精神相往來。\\n威廉‧詹姆士 (William James)曾描述他心目中「真正的哈佛」，他說：我相信，就培植自主與孤獨的思想者的苗床而言。除了哈佛大學，無出其右者。哈佛的環境不祇允許，而且鼓勵人們從自己的特立獨行之中尋得快樂。相反的，倘若有天哈佛想把她的孩子塑模成單一固定的性格，這將是哈佛的末日。\\n筆者毫無異議地贊成詹姆士的想法。\\n的確，世界上還有不少大學的歷史比哈佛更悠久，還有不少大學的校園比哈佛更美麗，還有不少大學的精神比哈佛更貴族；但對筆者而言，只有哈佛才是心靈的故鄉。\\n=================================\\n三十年前，怀着前往“西方取经”的宏愿，远渡重洋，到哈佛进修。其时名师云集，诸如哲学的罗尔斯、心理学的科尔伯格、社会学的贝尔、思想史的史华慈，百家齐鸣，交织成一部波澜壮阔的交响曲。身临其境，聆听人间知识的曲目，何其幸运。虽然随着岁月的流逝，大师逐一陨落，哈佛也迈入一个“无大师的时代”。但这种自由、多元、独立的学术精神，却深植我心，且影响了日后自己治学的取向，而受用无穷。诚如詹姆斯所言，哈佛教育的最终目的，在于培养独立自主的人格。因此，真正的哈佛人无需魂牵梦萦，而是勇往直前，开疆辟土，追寻美丽的新世界。', '哈佛琐记', '[]', '0'), ('1534620698', '1', '42', '[\"[\\u7f8e]\\u96f7\\u8499\\u5fb7\\u00b7\\u5361\\u4f5b\"]', '诗歌', 'https://img1.doubanio.com/lpic/s11337287.jpg', '9787544715690', '272', null, '2012-9', '译林出版社', '', '卡佛作品新推文学合集，诗歌首次大量露面\\n---------------------------------------------------------------------------\\n本书收入卡佛的短篇小说、随笔及诗歌。小说方面，卡佛特别不满意《纽约客》文学编辑利什对《洗澡》、《所有的东西都粘在他身上》和《咖啡先生和修理先生》的删节，他改了标题，大量修改内容，都收入《火》里。《谎话》、《木屋》等短篇小说则是首次有中译本。诗歌方面，收录《你们不知道什么是爱》《在克拉马斯河附近》《我父亲二十二岁时的照片》等著名作品。随笔方面，有《关于写作》《我父亲的一生》《火》等文学爱好者期待已久的篇目。\\n---------------------------------------------------------------------------\\n编辑推荐：\\n•20世纪下半叶美国最重要作家，村上春树的文学老师，苏童、格非、韩东热爱的人；\\n•继《大教堂》《当我们谈论爱情时我们在谈论什么》后，卡佛作品又出新作；\\n•首次全面展现卡佛文学成就：小说、诗歌和随笔；\\n•“诗人”是卡佛的重要身份，卡佛诗歌首次大量露面，同样带着简朴的力量，被认为“更生动地展现生活图景”，“令人气喘，发颤，陷入敬畏”；\\n•与《谈爱》相比，整体回归到一种更加饱满的写作风格；', '火', '[\"\\u5b59\\u4ef2\\u65ed\"]', '0'), ('1534620698', '1', '43', '[\"[\\u79d8\\u9c81]\\u4f0a\\u838e\\u8d1d\\u5c14\\u00b7\\u963f\\u8fde\\u5fb7\"]', '随笔', 'https://img3.doubanio.com/lpic/s8987616.jpg', '9787544702515', '328', null, '2007-4', '译林出版社', '感官回忆录', '这是一次在感官记忆的领域里不带地图的旅行，作者以这种方式记忆每一个走过她生命的男人，有人是皮肤的纹路，有人是亲吻的味道、衣服的气味、呢喃的声音，而且几乎每个人都令她想到某种特别的食物，都会让某个特别的男人重现我眼前，那种火腿乳酪夹心面包，唤回他们最美好的拥抱，而那种德国葡萄酒，正是他嘴唇的味道……多年前的旧情像恋恋难舍的鬼魂那般坚持，回头来在暮年点燃一把淘气的野火。', '阿佛洛狄特', '[\"\\u5f20\\u5b9a\\u7eee\"]', '0'), ('1534620707', '1', '44', '[\"(\\u7f8e)\\u4e54\\u7eb3\\u68ee\\u00b7\\u5c3c\"]', '历史', 'https://img3.doubanio.com/lpic/s3431201.jpg', '9787508612416', '237', null, '2008年', '中信出版社', '华尔街10年变迁内幕', '过去的10年，见证了投资银行业的热闹纷呈，也交替着经济的繁荣与衰落。高盛、摩根士丹利、美林、雷曼、贝尔斯登，这些响当当的名字曾经让无数聪明而富有野心的年轻人心生向往、顶礼膜拜。投资银行家这群神秘、富有、极具权势的社会精英，是如何操控价值上百亿美元的大宗并购，影响商业巨头、企业财团重大商业决策的？\\n《半路出家的投资银行家：华尔街10年变迁内幕》作者乔纳森·尼是一个半路出家的投资银行家。一次偶然的机会，让他进入了华尔街极具声望的高盛银行，而后跳槽到摩根士丹利。他见证了这个行业经历的一段特殊时代——因互联网经济高速发展而出现惊人业务量的时代，一个令人振奋的年代，以及之后随泡沫破灭而出现的灾难。作者以促人深省的幽默和无所畏惧的坦诚，揭开了投资银行家的神秘面纱，展现了投行业的原始面貌，让我们得以一窥那些充满魅惑力的故事——藏于华尔街最有权势的公司背后的故事。', '半路出家的投资银行家', '[\"\\u8d1d\\u591a\\u5e7f \\u53f6\\u626c \\u8983\\u626c\\u7709\"]', '0'), ('1534620708', '1', '45', '[\"[\\u65e5] \\u4ed3\\u7530\\u767e\\u4e09\"]', '日本文学', 'https://img3.doubanio.com/lpic/s1236886.jpg', '9787538264999', '151', null, '2003-3', '辽宁教育出版社', '', '「世俗是火宅，生比死痛苦得多。\\n能坚强地忍耐着，信仰之心就会萌生。」\\n一个僧侣与艺妓的故事，一场狂暴的宗教与情爱冲突；\\n一个高僧与浪子的故事，至死难解的亲情纠葛。\\n这部剧作不仅刻划了净土真宗的开门祖师，亲鸾。\\n还展现人生充满矛盾的现实──\\n纯洁之心与肉欲的诱惑、信仰与怀疑、善与恶、生与死、罪与罚……\\n★影响日本，乃至世界文坛的青春史诗！\\n法国文豪罗曼．罗兰深受感动！\\n德国哲学家海德格尔大为赞赏！\\n三岛由纪夫更是为之倾倒，据说他在切腹自杀前不久，还在重读。\\n「人生最重要的就是生死的事情。我家里人丁不旺，只有七个人，十六岁的时候阿嬷突然过世，我开始想人为何会死、死了又如何。后来我读到对我一生影响最大的三本书，就是托马斯．卡莱尔的《衣裳哲学》、仓田百三的《出家及其弟子》、哥德的《浮士德》。这三本书都是在讲人活着的意义。」——李前总统登辉先生 《财讯》 第375期\\n★一部哀伤的青春史诗！\\n《出家及其弟子》是作者仓田百三于26岁写成的一部剧本，初刊于1917年创刊的杂志《生命之河》。当时他已重病缠身，卧床不起。他在22岁就染上结核病，被迫辍学，直到40岁仍无法摆脱病榻，其作品几乎都是在与疾病苦战的情况下写成。对一个时时刻刻都受到死亡威胁的人来说，对于生命的理解极为深刻。\\n他在病床上所经历的不仅仅是肉体的煎熬，更残酷的是精神上的重压，那种对灵魂鞭挞的痛苦使仓田进入了宗教性的体验，同时他自身天才般的艺术感受又令他近乎于窒息。\\n致使他吶喊道︰「命运蹂躏着希望」；\\n也自我安慰着︰「祈愿呼唤着命运。」\\n★一部横跨世纪的经典之作！\\n本书在日本由岩波书店于1918年发行单行本以后，在日本社会引起了强烈的回响，一版再版，直到如今，岩波书店已经重印了86次以上，其它出版社如新潮社，讲谈社等多家大型出版社纷纷发行单行本，而且累积再刷次数多达百次以上。一部剧作横跨整个二十世纪能够拥有如此众多的读者，在日本的文学界，尤其是明治维新以后，实属罕见。\\n「我相信，哪怕是恶，也能依靠其它法门抵达乐土；这就是爱，是宽容，是超越善和恶而发挥的作用。」\\n《出家及其弟子》故事描述，开创净土真宗的日本佛教大师亲鸾，早年因受诬陷，被判流放，在各地苦行巡礼。期间与一名武士的女儿生下了一个男孩，善鸾。并在某个下大雪的夜里，因欲借宿与一个猎户人家结了缘份。\\n十五年后，亲鸾大师带领弟子们建立起净土真宗的威望。猎户的儿子松若也出家修行改名「唯圆」，并跟随、近身服侍亲鸾，成为亲鸾最亲近的弟子。\\n但亲生儿子善鸾却终日酒肉池林，不愿接受信仰的救赎，被世俗认作放荡儿。甚至透过善鸾的介绍，唯圆爱上了一名叫做「枫」的艺妓，让原本平静无波的寺院掀起了一场信仰与道德的风暴。……\\n亲鸾能否原谅接纳善鸾？善鸾是否接受佛法的洗礼？唯圆如何在信仰与情爱中求得平衡？\\n一段佛缘、亲情、情爱相互交缠的纠葛错乱。\\n众生们能否求得超脱一切烦恼的智慧？抑或，只是不断为彼此套上更多枷锁', '出家与其弟子', '[\"\\u6bdb\\u4e39\\u9752\"]', '0'), ('1534620708', '1', '46', '[\"\\u674e\\u53d4\\u540c\"]', '回忆录', 'https://img3.doubanio.com/lpic/s4619554.jpg', '9787508047744', '216', null, '2008-8', '华夏出版社', '李叔同回忆录', '自述·日记\\n我在西湖出家的经过\\n西湖夜游记\\n余弘律之因缘\\n南闽十年之梦影\\n惠安弘法日记\\n壬丙南闽弘法略志\\n行脚散记\\n断食日志\\n书信是·来\\n致徐耀廷\\n致海上同人\\n致许幻园\\n致杨白民\\n致毛子坚\\n致周啸麟\\n致陆丹林\\n致叶舟\\n致日本东京美术学校图书馆\\n致刘质平\\n致王仁安\\n致夏丐尊\\n致印心、宝善和尚\\n致李圣章\\n致李圆净\\n致蔡元培、经亨颐、马叙伦等\\n致瑞今法师\\n致仁开法师\\n致林奉若\\n致律华法师\\n佛学·讲演\\n初发心者在家律要\\n劝人听钟念佛文\\n净土法门大意\\n授三皈依大意\\n放生与杀生之果报\\n敬三宝\\n常随佛学\\n切莫误解佛教\\n泉州开元慈儿院讲录\\n万寿岩念佛堂开堂演词\\n律学要略\\n青年佛徒应注意的四项\\n佛法大意\\n劝念佛菩萨求生西方\\n佛法宗派大概\\n佛法十疑略释\\n佛法学习初步\\n佛教之简易修持法\\n略述印光大师之盛德\\n为性常法师掩关笔示法则\\n药师如来法门一斑\\n人生之最后\\n改习惯\\n最后之口口\\n消息·杂志\\n日本文艺消息\\n《太平洋报》出版祝词\\n《太平洋报》破天荒最新式之广告\\n广告丛谈\\n醵纨阁李漱筒润格\\n李漱筒重订书例\\n李叔同书例\\n广告部消息\\n文美会消息\\n城东女学消息\\n征求滑稽讽刺画稿\\n征求小学校中学校女学校学生诸君毛笔画\\n征求学生毛笔画新规则\\n书刊介绍\\n乐石社记\\n乐石社社友小传\\n朽道人传\\n附录李叔同年谱\\n……', '我在西湖出家的经过', '[]', '0'), ('1534620708', '1', '47', '[\"\\u5218\\u4e66\\u5b8f\", \"\\u8d24\\u4e66\\u6cd5\\u5e08\"]', '佛教', 'https://img3.doubanio.com/lpic/s27765350.jpg', '9787535476593', '240', null, '2014-12', '长江文艺出版社', '在人生的转角处遇到了佛祖', '从觥筹交错、商海沉浮的老板，到青灯佛影、一心向佛的僧侣；\\n从炙手可热、风靡一时的网络红人，到辞亲割爱、剃度出家的人生无常。\\n讲述一个你未曾经历过的出家生活，探寻一段见自己，见天地，见众生的佛学智慧。\\n一个成功商人、网络红人、编剧、广告策划人、丈夫和父亲，最终放弃一切，剃度出家。更令人吃惊的是，妻子和两个孩子也跟着他学佛修道，追寻他们理解的快乐。这是怎样的一家人？\\n本书分为四个部分来讲述，出家、修行、学佛、感悟人生。给我们交代自己半路出家的心路历程，同时也是帮助修行者在生活和学修中，让自己的行为来符合佛法，而不是用佛法来解释你的行为。用柔和的笔锋，清晰透彻的思想纹路，表达那些文化的、智慧的、精巧的，绝妙的思想片段。传授一种力量，一种认识，一种态度，一种悟性的心灵小品。\\n编辑推荐：\\n1、《烦恼都是自找的》的作者贤书法师，最新力作！\\n2、 从成功人士走向阿弥陀佛：一位成功商人、网络红人、鬼才编剧、广告界精英，辞亲割爱，剃度出家的人生无常。讲述一个你未曾经历过的出家生活，探寻一段见自己，见天地， 见众生的佛学智慧。\\n3、	写给凡心世人的生活修行书，一本通俗好读的入世经典！\\n4、对所有在红尘中还为“得不到”、“放不下”而烦恼的人，尤其是郁闷、忙碌、焦虑的上班族，传授一种学会放下，学会自在，学会淡定的心智。\\n5、	一个个发人深省、启人心智并充满感恩的出家历程，用真实的例子为读者解决现实生活中所要面对的种种问题提供了有益的参照，能净化心灵，助你脱离烦恼，心生菩提。\\n6、	在轻松的阅读中获得启示、反省自身，为繁忙的都市人带来了清新的阅读体验和思考空间。', '半路出家', '[]', '0'), ('1534620720', '1', '48', '[\"\\u5e7f\\u53d1\\u8bc1\\u5238\\u4e92\\u8054\\u7f51\\u91d1\\u878d\\u6280\\u672f\\u56e2\\u961f\"]', '编程', 'https://img3.doubanio.com/lpic/s29295733.jpg', '9787121306501', '504', null, '2017-1', '电子工业出版社', '', '《揭秘Angular 2》第一部分从前端的故事起点说起，然后对 Angular 以及 TypeScript 进行了简单的介绍，接着通过一个通讯录例子让读者快速入门 Angular 的开发；第二部分则深入讲解了 Angular 架构以及 Angular 核心内容，包括组件、模板、指令、服务、依赖注入、路由以及测试，此外，在相应的章节里还补充说明了譬如变化监测的核心 Zones（第 6 章）、双向绑定的原理（第 7 章）、RxJS（第 9 章）等关键内容；第三部分则通过问卷调查系统来指引读者进行 Angular 项目的实战。', '揭秘Angular 2', '[]', '0'), ('1534620720', '1', '49', '[\"\\u3010\\u7f8e\\u3011Artur Ejsmont\"]', '编程', 'https://img3.doubanio.com/lpic/s29131113.jpg', '9787121301124', '324', null, '2016-12', '电子工业出版社', '', '可伸缩架构技术是所有互联网技术中最重要，也是最引人入胜的技术。《互联网创业核心技术：构建可伸缩的web应用》针对互联网创业需求快速迭代，业务快速发展，短时间内用户、数据、访问量激增的特点，提纲挈领地描述了伸缩性架构的基本原理与设计原则，详细阐述了Web应用前端层、服务层、数据层的可伸缩架构，并花大量篇幅讲述了缓存技术和异步处理技术的可伸缩设计及其在Web系统中的具体应用。\\n《互联网创业核心技术：构建可伸缩的web应用》面向互联网创业公司工程师，也适用于所有互联网行业的工程师，对非互联网行业的软件工程师也有借鉴作用。事实上，《互联网创业核心技术：构建可伸缩的web应用》适合所有对可伸缩架构有兴趣的软件技术人员阅读。', '互联网创业核心技术：构建可伸缩的web应用', '[\"\\u674e\\u667a\\u6167\", \"\\u4f55\\u5764\"]', '0'), ('1534620720', '1', '50', '[\"\\u897f\\u95e8\\u67f3\\u4e0a\", \"\\u9a6c\\u56fd\\u826f\", \"\\u5218\\u6e05\\u534e\"]', '互联网', 'https://img1.doubanio.com/lpic/s6071149.jpg', '9787111282259', '226', null, '2009-10', '机械工业出版社华章图书', '全球互联网将进入SNS时代', '短短20年里，互联网经历了门户网站和Web 2.0时代的重大变革，如今已经迈入社会化网络时代。新一轮的互联网革命即将爆发，而其影响已经开始……\\n本书详细介绍了SNS（社会化网络）的起源和理论模型、SNS发展的三波浪潮，以及Facebook、Twitter、校内网、开心网和51等国内外各大主流SNS网站的发展历程和成功秘诀；全面阐述了SNS将对我们的生活、工作、学习、娱乐方式产生的颠覆性影响；透彻地分析了SNS与各种传统互联网经营模式相结合后会带来的变革和机遇，以及SNS开放平台的“长尾效应”和它对整个互联网产业的影响和重大意义；最后，对SNS在3G移动互联网时代的前景进行了分析和预测，同时对SNS的未来进行了展望，描绘了一幅美好的SNS时代全景图。', '正在爆发的互联网革命', '[]', '0'), ('1534620720', '1', '51', '[\"[\\u82f1] \\u5b89\\u5fb7\\u9c81\\u00b7\\u67e5\\u5fb7\\u5a01\\u514b\"]', '互联网', 'https://img1.doubanio.com/lpic/s24958789.jpg', '9787508057316', '524', null, '2010-4', '华夏出版社', '国家、公民与新传播技术', '《互联网政治学:国家、公民与新传播技术》研究了新传播技术对政党与选举、压力集团、社会运动、地方民主、公共机构和全球治理的影响。《互联网政治学:国家、公民与新传播技术》还分析了持续性的和争议性的政策问题，这包括数字鸿沟，互联网自身的治理，监视、隐私与安全之间的张力，互联网媒体领域的政治经济学。《互联网政治学:国家、公民与新传播技术》采用比较性研究的方法，提供了美国、英国和其他许多国家的大量案例。写作风格清晰而易于理解，这本最新的极具复杂性的理论书籍，揭示了互联网对我们如何“从事”政治以及如何理解政治生活所产生的重要影响。\\n媒体推荐\\n传播技术的政治以及传播技术与政治，是所有新的传播技术问世前后都会牵涉到的问题。而在现代政治社会中，无非将形成两组关系，一是国家与新传播技术，二是公民与新传播技术。前者是庙堂之上的云霄，后者是草根之下的土壤。它们之间尽管有此霄壤之别，但却相辅相成，这正是互联网链接式政治生态的奇观之一……《互联网政治学》的讨论正是遵循了这样一种逻辑。\\n——南京大学新闻传播学院教授 杜骏飞\\n本书的优点在于：具有典范性的主题架构、极其透彻的研究、对所涉问题的每个重要文献或观点的归纳。在互联网影响社会的文献中，该书将具有突出的贡献。\\n——得克萨斯大学奥斯汀分校 加里·查普曼\\n截至目前，查德威克提供了对互联网与政治这一领域的最为全面的理解。这本书的价值就在于论述了21世纪最具决定性的技术政治。这是一本令人永世难忘的著作。\\n——华盛顿大学 兰斯·本内特\\n本书是互联网与政治这一领域中最为透彻和全面的著作——查德威克以其对研究文献的饱学博览，给初涉此领域的学生提供了最为细致的导论性资料。\\n——加州大学芭芭拉分校 布鲁斯·比姆博', '互联网政治学', '[\"\\u4efb\\u5b5f\\u5c71\"]', '0'), ('1534620720', '1', '52', '[\"\\u6bb5\\u6c38\\u671d\"]', '互联网', 'https://img3.doubanio.com/lpic/s6094206.jpg', '9787508617039', '313', null, '2009-11', '中信出版社', '', '《互联网:碎片化生存》内容简介：在世界互联网人数超过17亿，中国网民接近4亿的时候，断言“这个版本的互联网没有未来”是要冒很大风险的。我们生活在比特和连线的世界，现代互联网所描绘出的“数字化”、“虚拟化”的未来是否完全值得信赖？\\n现代商业取得了巨大成功，但这并不是电脑和互联网精髓的自由体现，我们所使用的这个版本的电脑和互联网只不过是“被阉割”、“被劫持”的商业玩偶。\\n《互联网:碎片化生存》作者通过深入研究和剖析电脑与互联网的发展史，指出这个版本的互联网本质上是“笛卡尔式的”，“玩弄碎片”是它造成的结果！当网民达到一定数量时，虚拟空间必会导致某种根本性的变革，这就是“碎片化”和“虚拟化”。在虚拟世界中，没有“我”和“他”，只有“肉”，“独立存在的个体”必将走向“多个版本存在的个体”。', '互联网：碎片化生存', '[]', '0'), ('1534620720', '1', '53', '[\"[\\u7f8e]\\u52b3\\u62c9\\u00b7\\u91cc\\u65af\"]', '互联网', 'https://img1.doubanio.com/lpic/s1508618.jpg', '9787208060036', '204', null, '2006-01-01', '上海人民出版社', '摩根士丹利所推崇的商业战略思想', '随着我们迈入21世纪，对于商业而言最重要的问题是：我们将如何应对互联网?阿尔·里斯和劳拉·里斯是全美国最有资格回答这个问题的人物。看过本书后，你将知道： ·互联网不能身兼二职 ·互动性是网站最重要的要素 ·互联网品牌使用通用名就是自寻死路 ·做品类中的第二位品牌将没有出路 ·第一个进入顾客的心智并且集中焦点 ·最严重的错误莫过于相信自己无所不能 两位作者具有个性鲜明的反主流文化的视角以及独到的营销常识，并用他们的专业所长解决了打造互联网品牌这个当今营销界最具挑战性的问题。每一个希望打造全球大品牌的人都不应该错过他们的高见。', '互联网商规11条', '[\"\\u6885\\u6e05\\u8c6a\", \"\\u5468\\u5b89\\u67f1\"]', '0'), ('1534620720', '1', '54', '[\"Mull He\"]', '互联网', 'https://img3.doubanio.com/lpic/s6521194.jpg', '9787121135262', '211', null, '2011-6', '电子工业出版社', '与中国五亿网民互为影响的互联网DNA', '《互联网之达芬奇密码-浪潮揭秘:与中国五亿网民互为影响的互联网…》，本书特别之处在于用分章节的形式来讲述了中国互联网十多年发展历程中各个阶段的真实用户需求、企业发展、产业历程。在对应领域和时代背景的基础上，以独到的理论来分析中国互联网每一次变革与用户需求演进之间的潜在关系。', '互联网之达芬奇密码', '[]', '0'), ('1534620720', '1', '55', '[\"\\u9ec4\\u4eae\\u65b0\"]', '互联网', 'https://img3.doubanio.com/lpic/s6425713.jpg', '9787121134272', '188', null, '2011-4', '电子工业', '网站运营之人性、策略与实战', '《互联网创业前奏曲第二部:网站运营之人性、策略与实战》是《互联网创业前奏曲》系列的第二本书，是作者多年互联网实践经验和业界观察的总结，是国内罕有的关于互联网网站运营和用户心理结合的书籍，用通俗的语言阐述互联网运营背后的人性驱动。\\n你想互联网创业吗？你是否在为找不到好的互联网运营策略和方法而发愁？你想了解互联网行业？你是否在为自己不了解互联网运营而苦恼？《互联网创业前奏曲第二部:网站运营之人性、策略与实战》针对这些问题列举了很多互联网运营的案例，帮你制定运营策略，更好地修炼和提升运营功力。\\n《互联网创业前奏曲第二部:网站运营之人性、策略与实战》适合进入互联网行业不久的从业人员、希望从事互联网行业的大学生、关注互联网创业的人士、想了解和提升互联网运营技能的人员，以及准备和正在从事电商行业的相关人士阅读。本书不是一本秘籍，却可以成为你的知心助手。', '互联网创业前奏曲第二部', '[]', '0'), ('1534620720', '1', '56', '[\"\\u5218\\u4f1f\", \"\\u6587\\u98de\\u7fd4\\uff08\\u7f16\\uff09\"]', '互联网', 'https://img1.doubanio.com/lpic/s6170997.jpg', '9787302225027', '245', null, '2010-06', '清华大学出版社', '2010年全球最值得模仿的230个网站', '互联网创业成为当前很多人，特别是大学生、青年朋友创业门槛最低的入口，但互联网创业并不像人们想象的那么容易，互联网创业最需要的是点子，有了好的创意，可能就成功了一半，这对任何一个创业人，不仅是互联网创业都是十分重要的。\\n本书从当前全球数以千万计的网站中选出近一年来最具创意的230家网站进行介绍，其中网站的奇思妙想，令人叹为观止，开阔眼界，给人以启示，使人产生联想的火花。对于想在互联网上创业的青年人、大学生、经常和互联网打交道的各类人员，都极具参考价值。', '复制互联网', '[]', '0'), ('1534620720', '1', '57', '[\"[\\u7f8e] \\u6770\\u5f17\\u91cc\\u00b7\\u65af\\u8482\\u4f2f\"]', '互联网', 'https://img3.doubanio.com/lpic/s4524205.jpg', '9787508623313', '179', null, '2010-11', '中信出版社', '', '一本预示着Web4.0网络大时代来临的开创性读物\\n在这个重视营销宣传的社会，只要利用好互联网，就能让你商业上的竞争对手抓狂。大脑研究科学家和企业行为研究专家杰弗里•斯蒂伯在《我们改变了互联网，还是互联网改变了我们？》这本书中告诉你的是互联网的本质。互联网并不是简单地把各台电脑连在一起，而是把我们大脑的思维方式在身体的外部进行复制。为了利用好互联网的巨大力量，你首先要做的便是了解我们的大脑和互联网之间的相似性。这也就是写作这本书的一个动力所在。斯蒂伯在这本书中告诉了人们如何充分发挥互联网的力量：\\n1、互联网发生了怎样的变革，这对人们的沟通和社群的建立意味着什么。\\n2、因特网——或者在线商务的机遇，他们的未来是什么。\\n3、人工智能的下一步发展趋势是什么，它会为未来的商业发展带来什么机遇。\\n斯蒂伯在这本书中显示了那些取得卓越业绩的企业是如何深入理解互联网与大脑的内涵，从而在竞争中发挥自己的优势的——例如建设更为方便快捷的网址、预测消费者行为、利用社会传媒，最终建立一种集体意识。', '我们改变了互联网，还是互联网改变了我们?', '[\"\\u674e\\u6615\"]', '0'), ('1534620720', '1', '58', '[\"[\\u7f8e] Patricia Wallace\"]', '互联网', 'https://img3.doubanio.com/lpic/s1237880.jpg', '9787501929863', '272', null, '2001-1', '中国轻工业出版社', '', '《互联网心理学》成书于2001年，一经出版就在心理学界引起广泛关注。这是一部从心理学角度介绍互联网的科普著作，它为人们理解互联网中的心理、社会和文化提供了一个框架。大量的令人着迷的细节描述，把读者从熟悉的现实生活带入了虚拟的世界。', '互联网心理学', '[\"\\u8c22\\u5f71\", \"\\u82df\\u5efa\\u65b0\"]', '0'), ('1534620720', '1', '59', '[\"\\u9ec4\\u4eae\\u65b0\"]', '互联网', 'https://img3.doubanio.com/lpic/s24964256.jpg', '9787121066535', '253', null, '2008-7', null, '网站策划九步走', '《互联网创业前奏曲:网站策划九步走》阐述了互联网的商业本质、网站的赢利模式以及网站策划的细节，从实践操作的角度叙述了互联网行业的创业和策划需要具体做哪些工作，这些工作包括了如下九个部分：网站创业前的调研准备、网站的定位、网站的内容策划、设计策划、易用性策划、技术策划、策划文档及项目实施、推广策划以及运营策划。', '互联网创业前奏曲', '[]', '0'), ('1534620720', '1', '60', '[\"\\u5f17\\u91cc\\u66fc\\u30fbJ\\u30fb\\u6234\\u68ee\"]', '互联网', 'https://img3.doubanio.com/lpic/s2310046.jpg', '9787108014429', '153', null, '2000-07', '三联书店', '科学革命的工具', '本书是“科学人文”丛书之一。太阳、基因组与互联网是我们已经进入的新时代中社会发展的驱动力。这不仅是老科学家、美国物理学家戴森对“科学革命主要由概念变迁所主导”的观念的宣战，更强调了技术是科学的工具，随科学的变化而演进。支持这种观点的是雷达、微波、DNA研究、双螺旋，分子生物学等不断更新的学科。他乐观地看到，科学技术人员不固步自封，重视实践，勇于实践。就能使太阳能技术，遗传工程和通讯技术得到发展', '太阳、基因组与互联网', '[\"\\u8983\\u65b9\\u660e\"]', '0'), ('1534620720', '1', '61', '[\"\\u9a6c\\u514b\\u00b7\\u6ce2\\u65af\\u7279\"]', '互联网', 'https://img1.doubanio.com/lpic/s6796017.jpg', '9787564901424', '227', null, '2010-4', '河南大学出版社', '', '本书主要内容包括: 第一章文化的不确定性, 第二章技术之存在, 第三章资本主义的语言学转向, 第四章数字化主体与文化理论, 第五章模拟作者与数字化作者等。', '互联网怎么了?', '[\"\\u6613\\u5bb9\"]', '0'), ('1534620720', '1', '62', '[\"(\\u7f8e)\\u51ef\\u8328\\u7b49\"]', '互联网', 'https://img1.doubanio.com/lpic/s5831159.jpg', '9787100052320', '561', null, '2007-5', '商务印书馆', '', '《互联网使用的社会影响》介绍了美国互联网的现状，并从三个领域探讨互联网的社会影响，分别是接触互联网技术、通过互联网参与群体和社区以及使用互联网进行社会互动和表达。', '互联网使用的社会影响', '[]', '0'), ('1534620720', '1', '63', '[\"\\u66f9\\u653f\"]', '互联网', 'https://img1.doubanio.com/lpic/s29151227.jpg', '9787505738751', '272', null, '2016-12', '中国友谊出版公司', '从技术思维到商业逻辑', '为什么有人可以预见商机、超越景气，在不确定环境下表现更出色？\\n在规则之外，做好互联网，还有哪些关键秘诀？\\n当环境不给机会，你靠什么翻身？\\n本书为“互联网百晓生”曹政20多年互联网经验的总结，以严谨的逻辑思维分析个人与企业在互联网发展中的一些错误思想及做法，并给出正确解法。\\n从技术到商业如何实现，每个发展阶段需要匹配哪些能力、分解哪些目标、落实哪些策略都一一点出，并在这一过程中，呈现实操性方法背后的思考过程，即为什么这样做才是对的。\\n本书特点：\\n1、知识量大，盘点整个互联网发展历程。\\n2、见解深刻，抛开表象，重在解决问题。\\n3、客观不浮躁，一切从逻辑出发。\\n4、高效接地气，全是摸爬滚打总结出来的方法。\\n本书功效：\\n1、全面通晓互联网行业的固有逻辑。\\n2、在技术、产品上避免犯致命错误。\\n3、知道自己的不足与问题在哪里，并知道如何改正。\\n4、建立正确的互联网思维。\\n5、精准、高效地创业。\\n6、学会正确思考。', '你凭什么做好互联网', '[]', '0'), ('1534620720', '1', '64', '[\"\\u674e\\u5c11\\u52a0\"]', '互联网', 'https://img3.doubanio.com/lpic/s29195996.jpg', '9787121301780', '252', null, '2016-11', '电子工业出版社', '', '互联网运营作为一个新兴的岗位，一方面它是企业的核心岗职，身负重任，另一方面，又由于其短暂的历史，缺乏成熟体系的工作方法论，而目前业界主流的运营方法却是从企业视角出发，存在极大的改进空间。\\n《进化式运营：从互联网菜鸟到绝顶高手》作者基于自身十年的互联网洞察、实践经验，并融合了信息论、心理学、经济学、管理学、甚至包括生态学、进化论等跨学科跨学业的知识，从无到有地构建了一套全新的互联网运营体系：基于用户视角的用户养成运营框架，并从产品运营过程中的四大生命周期入手，阐述如何灵活地将该运营框架运用于产品的各个生命周期。从而彻底地使所有运营从业者能够从各种运营困惑中解脱出来，由内而外地激发运营思路，从根本上提升运营工作成效，进而打造个人的核心运营竞争力。', '进化式运营：从互联网菜鸟到绝顶高手', '[]', '0'), ('1534620720', '1', '65', '[\"\\u9648\\u5b87\\uff08\\u6c5f\\u5357\\u6124\\u9752\\uff09\"]', '互联网', 'https://img3.doubanio.com/lpic/s27303140.jpg', '9787506074186', '372', null, '2014-6-1', '东方出版社', '', '随着中国互联网金融浪潮高涨，P2P、众筹、余额宝、微信支付等新生事物层出不穷，加之大数据等时髦概念助阵，简直是乱花渐欲迷人眼，令媒体兴奋，公众狂热。那么，互联网金融真的能“颠覆”传统金融吗？当互联网思维对撞传统金融观念，是互联网金融的一统天下，还是传统金融业的自我革新？究竟是谁动了金融业的奶酪？\\n本书作者早期试水创立具有互联网金融雏形的网站，后来成为互联网金融的资深投资人，基于其多年在该领域的实践、观察与思考，追根溯源谈互联网金融之争，告诉你在变革大潮来袭时，应该做什么。', '风吹江南之互联网金融', '[]', '0'), ('1534620721', '1', '66', '[\"\\u6bb5\\u6c38\\u671d\"]', '互联网', 'https://img3.doubanio.com/lpic/s27844475.jpg', '9787100107488', '449', null, '2014-10', '商务印书馆', '北大讲义', '本书是作者为北京大学新闻与传播学院硕士生开设的《互联网前沿思想》课程的讲义。作者力图从技术、经济和社会的角度，在大尺度上观察互联网究竟根植于什么样的文化土壤。作者选择了复杂性、社会网络分析、公共空间这三个维度展开分析，为读者呈现出了脱胎于工业时代的互联网继承了哪些思想，并对哪些思想做出了彻底的颠覆。', '互联网思想十讲', '[]', '0'), ('1534620721', '1', '67', '[\"\\u674e\\u5584\\u53cb\"]', '互联网', 'https://img3.doubanio.com/lpic/s27898183.jpg', '9787111482727', '272', null, '2014-12-1', '机械工业出版社', '移动互联网时代的生存法则', '为什么把每件事情都做对了，仍有可能错失城池？为什么无人可敌的领先企业，却在一夜之间虎落平阳？\\n短短三年间诺基亚陨落，摩托罗拉区区29亿美元出售给联想，芯片业霸主英特尔在移动芯片领域份额几乎为零，风光无限的巨头转眼成为被颠覆的恐龙，默默无闻的小公司一战成名迅速崛起，令人瞠目结舌的现象几乎都被“颠覆式创新”法则所解释。颠覆式创新教你在新的商业竞争中“换操作系统”而不是“打补丁”，小公司用破坏性思维可以“以小博大”占领新兴市场。大公司进行自我攻击，才是唯一活下去的机会，今天，全人类都面临一个在新“价值网”中生存的大悬难。\\n《颠覆式创新：移动互联网时代的生存法则》从哲学、物理、思维、管理多个维度，探索已知世界的边界，透过费解的现象，揭示商业社会中令人惊悚的真相。不确定的世界里，探索比结论重要，移动互联网时代，见识比知识更重要。', '颠覆式创新：移动互联网时代的生存法则', '[]', '0'), ('1534620731', '1', '68', '[\"\\u6e29\\u65bd\\u8010\\u5fb7\"]', '编程', 'https://img3.doubanio.com/lpic/s1856795.jpg', '9787111192367', '628', null, '2006-9', '机械工业出版社', '', '本书详细讲解了3个应用程序的构建过程:一个日程安排系统、一个博客引擎以及一个电子商务商店。细致分析如何创建健壮的Web数据库应用程序，以及如何避免困扰许多PHP和MySQL开发者的常见错语。\\n本书是为程序员提供的综合且实用的指南，教他们使用PHP和MySQL开发具有产品品质的、使用数据库的Web应用程序。本书不仅包括编写代码，而且介绍前端应用程序、用户界面和数据库设计，还有安全性、测试以及更多内容。\\n主要内容：\\n·从PHP开发的入门开始，包括代码组织、重用和面向对象的PHP。\\n·对数据访问详细讨论，包含事务处理、外键和索引。\\n·利用PHP正则表达式的功能强大的数据验证技术。\\n·介绍计划Web应用程序，包括用户界面和用户管理。\\n·提供关于保证Web应用程序安全性的系统化指南。\\n·涵盖广泛的实现问题——包括国际化、错语处理、数据验证、调试、会话管理和部署。\\n·关于XML、基于XML的Web服务和用于可重用PHP组件的PEAR框架。\\n·如何使用Oracle和PostgreSQL的数据库。\\n·本书配套光盘的内容本书的源代码（包括3个完整的应用程序）。', 'PHP和MySQL Web应用开发核心技术', '[\"\\u9a6c\\u671d\\u6656\"]', '0'), ('1534620731', '1', '69', '[\"Luke Welling\", \"Laura Thomson\"]', '编程', 'https://img3.doubanio.com/lpic/s3773304.jpg', '9787111262817', '714', null, '2009', '机械工业出版社', '', '本书将PHP开发与MySQL应用相结合，分别对PHP和MySQL做了深入浅出的分析，不仅介绍PHP和MySQL的一般概念，而且对PHP和MySQL的Web应用做了较全面的阐述，并包括几个经典且实用的例子。.\\n本书是第4版，经过了全面的更新、重写和扩展，包括PHP 5.3最新改进的特性（例如，更好的错误和异常处理），MySQL的存储过程和存储引擎，Ajax技术与Web 2.0以及Web应用需要注意的安全问题。...', 'PHP和MySQL Web开发(原书第4版)', '[\"\\u6b66\\u6b23\"]', '0'), ('1534620731', '1', '70', '[\"Josh Lockhart\"]', '编程', 'https://img3.doubanio.com/lpic/s28310875.jpg', '9787512380936', '219', null, '2015-9', '中国电力出版社', '', 'PHP正在重生，不过所有PHP在线教程都过时了，很难体现这一点。通过这本实用的指南，你会发现，借助面向对象、命名空间和不断增多的可重用的组件库，PHP已经成为一门功能完善的成熟语言。\\n本书作者Josh Lockhart是“PHP之道”的发起人，这是个受欢迎的新方案，鼓励开发者使用PHP最佳实践。Josh通过实践揭示了PHP语言的这些新特性。你会学到关于应用架构、规划、数据库、安全、测试、调试和部署方面的最佳实践。如果你具有PHP基础知识，想提高自己的技能，绝对不能错过这本书。\\n• 学习现代的PHP特性，例如命名空间、性状、生成器和闭包。\\n• 探索如何查找、使用和创建PHP组件。\\n• 遵从应用安全方面的最佳实践，将其运用在数据库、错误和异常处理等方面。\\n• 学习部署、调优、测试和分析PHP应用的工具和技术。\\n• 探索Facebook开发的HHVM和Hack语言。\\n• 搭建与生产服务器高度一致的本地开发环境。', 'Modern PHP（中文版）', '[\"\\u5b89\\u9053\"]', '0'), ('1534620731', '1', '71', '[\"Matt Zandstra\"]', '编程', 'https://img3.doubanio.com/lpic/s4261122.jpg', '9787115225788', '383', null, '2010-5', '人民邮电出版社', '面向对象、模式与实践（第2版）', '本书全面深入地剖析了面向对象的PHP编程与设计。首先介绍了PHP的OO特性，包括类声明、对象实例化、继承、方法与属性封装以及静态方法与属性、抽象类、接口、异常处理、对象克隆等高级主题。然后介绍了设计模式，阐述了模式的概念，展示了如何在PHP中实现几个关键的模式，用专门的章节介绍了企业模式和数据库模式。本书最后一部分围绕PHP应用程序开发，详细介绍了一批极为实用的辅助开发工具，讨论了具有普遍意义的最佳开发实践。\\n本书适合每位PHP程序员进阶参考，帮助他们掌握PHP面向对象设计和开发的精髓，跻身高端PHP程序员之列。', '深入PHP', '[\"\\u9648\\u6d69\", \"\\u5434\\u5b59\\u6ee8\", \"\\u80e1\\u4e39\"]', '0'), ('1534620731', '1', '72', '[\"Lynn Beighley\", \"Michael Morrison\"]', '编程', 'https://img3.doubanio.com/lpic/s6301934.jpg', '9787512305137', '774', null, '2010-6', '中国电力', '', '通过《深入浅出PHP&MySQL(影印版)》，你将学习：准备好把你的静态HTML网页提升到下一个层次并使用PHP和MySQL建立数据库驱动的网站了吗？《深入浅出PHP& MysQL》是一本快捷实用的指南，让你的动态网站快速运行。自己动手建立实际应用程序，从视频游戏高分留言板到在线交友网站。当你完成后，你将可以进行验证表单、使用会话ID和cookies工作、执行数据库查询和联接、处理文件I／0操作等等。\\n《深入浅出PHP&MySQL(影印版)》的特别之处在于：我们认为你的时间如此宝贵以至于不应该花费在为新概念伤脑筋上面。《深入浅出PHP ＆ MySQL》用最新的认知科学和学习理论打造多感官的学习体验，运用适合大脑工作方式的直观的格式编排，而不是令人昏昏欲睡的密密麻麻的文字。', 'Head First PHP & MySQL（中文版）', '[\"\\u82cf\\u91d1\\u56fd\", \"\\u5f90\\u9633\"]', '0'), ('1534620731', '1', '73', '[\"\\u5217\\u65ed\\u677e\\uff0c\\u9648\\u6587\"]', '编程', 'https://img3.doubanio.com/lpic/s27276430.jpg', '9787111401926', '522', null, '2013-1-1', '机械工业出版社', '', '《PHP核心技术与最佳实践》是一本致力于为希望成为中高级PHP程序员的读者提供高效而有针对性指导的经典著作。系统归纳和深刻解读了PHP开发中的编程思想、底层原理、核心技术、开发技巧、编码规范和最佳实践。全书分为5个部分：第一部分（1～2章）从不同的角度阐述了面向对象软件设计思想的核心概念、技术和原则，分析了面向对象的特性、设计模式的理念，指出了如何设计低耦合、高可扩展性的软件，等等；第二部分（3～6章）详细讲解了PHP中正则表达式的规范和使用技巧，PHP网络编程的原理、方法、技巧和一些重要的操作，PDO、数据库应用优化，数据库设计和MySQL的高级应用，PHP扩展引擎的原理与实践；第三部分（第7章）拨云见日，围绕PHP扩展开发进行了细致而深入的探讨，解析了PHP的底层实现和Zend虚拟机API，并用PHP扩展开发的实例带领读者走进PHP的底层世界，旨在让读者对PHP性能优化、底层原理进行深入的理解。第四部分（8～11章）重点讨论了缓存的设计、Memcached的原理与实践、NoSQL数据库Redis源码分析与应用实践、高性能PHP网站的架构和设计等内容；第五部分（12～14章）详细讲解了PHP代码的调试和测试、Hash算法和数据库的实现，以及PHP的编码规范，旨在帮助读者提高开发效率，养成良好编程习惯。', 'PHP核心技术与最佳实践', '[]', '0'), ('1534620731', '1', '74', '[\"\\uff08\\u7f8e\\uff09\\u65af\\u514b\\u62c9\", \"\\uff08\\u7f8e\\uff09\\u5207\\u8d1d\\u7279\\u4f2f\\u683c\"]', '编程', 'https://img3.doubanio.com/lpic/s4048154.jpg', '9787508386089', '816', null, '2009-10', '中国电力出版社', '', 'PHP经典实例（第2版）能够为您节省宝贵的Web开发时间。有了这些针对真实问题的解决方案放在手边，大多数编程难题都会迎刃而解。《PHP经典实例(第2版)》将PHP的特性与经典实例丛书的独特形式组合到一起，足以帮您成功地构建跨浏览器的Web应用程序。在这个修订版中，您可以更加方便地找到各种编程问题的解决方案，《PHP经典实例(第2版)》中内容涵盖了：表单处理；Session管理；数据库交互；使用Web服务。\\n从初学者常见的问题到高级Web编程技术，这本包含了丰富的、具有实际应用价值的实例图书，可以满足使用PHP生成动态web内容的任何人的需要。书中的每个实例都细致地讨论了所提供解决方案背后的逻辑和思想，用源自PHP专家的洞察力帮你轻松地掌握这门语言。书中更新了PItP5的有关内容，并详细地解释了如何使用新增的语言特性，比如面向对象能力的巨大改进和新的PD0数据访问扩展等。书中特别增加了有关类和对象的部分，包含了以下基本内容：处理XM；与JavaScript交互；用PHP构建Web服务；使用SOAP和REST架构。\\n《PHP经典实例（第2版）》中超过250个实例，为你每天都要面对的诸多问题提供了足够丰富的解决方案。而且，每个实例的讨论部分都浸透着对每个PHP开发人员极为有益的理念。', 'PHP经典实例', '[\"\\u674e\\u677e\\u5cf0\", \"\\u79e6\\u7eea\\u6587\", \"\\u674e\\u4e3d\"]', '0'), ('1534620731', '1', '75', '[\"W.Jason Gilmore\"]', '编程', 'https://img3.doubanio.com/lpic/s2160043.jpg', '9787115155092', '634', null, '2007年01月', '人民邮电出版社', '', '本书是久负盛名的经典著作，以涵盖全面详实而著称，对主题的选取和组织从实用出\\n发，在讲述知识之外还加入了作者自己的应用经验，并提供了密集的实战代码示例，充\\n分体现了作者深厚的开发功力。书中对PHP的介绍是迄今为止最为全面的', 'PHP与MySQL 5程序设计', '[\"\\u6731\\u6d9b\\u6c5f\"]', '0'), ('1534620731', '1', '76', '[\"(\\u7f8e)\\u53e4\\u66fc\\u5179\\u7b49\"]', '编程', 'https://img1.doubanio.com/lpic/s5860028.jpg', '9787121050787', '689', null, '2007-12', '电子工业出版社', '', '《BRUCE PERENS开源系列丛书•PHP 5权威编程》为大家全面介绍了PHP 5中的新功能、面向对象编程方法及设计模式，还分析阐述了PHP5中新的数据库连接处理、错误处理和XML处理等机制。希望能够帮助读者系统了解、熟练掌握PHP，最大程度地挖掘：PHP的潜力，以更低的成本搭建更加稳健、高效的PHP应用。\\n近年来，随着使用PHP的大流量网站逐渐增加，企业在使用PHP的时候开始面临新的问题，代码的规范与维护就是其中比较突出的问题。另外，在PIIPfl勺速度满足业务需求后，人们对项目的稳健性、可升级性提出了新的要求。', 'PHP 5权威编程', '[\"\\u7b80\\u5f20\\u6842\"]', '0'), ('1534620731', '1', '77', '[\"\\u9ad8\\u6d1b\\u5cf0\"]', '编程', 'https://img1.doubanio.com/lpic/s6080118.jpg', '9787121094415', '649', null, '2009-10', '电子工业出版社', '', '《细说PHP》开发Web应用程序PHP是最理想的工具，易于使用、功能强大、成本低廉、高安全性、开发速度快且执行灵活。《细说PHP》以实用为目标设计，包含PHP开发最主流的各项技术，对每一个知识点都进行了深入详细的讲解，并附有大量的实例代码，图文并茂。系统地介绍了PHP的相关技术及其在实际Web开发中的应用。\\n《细说PHP》共17章，每一章都是PHP独立知识点的总结。内容涵盖了PHP的运行环境搭建、Web服务器Apache的配置与应用、动态网站开发的前台技术、PHP编程语言的语法、PHP的常用功能模块和实用技巧、MySQL数据库的设计与应用、PHP 5面向对象的程序设计思想、Web开发的设计模式，以及包含DIV+CSS、mysqli扩展模块、数据库抽象层PDO、Smarty模板技术等目前PHP开发中最主流的技术。每一章中都有大量的实用示例，以及详尽的注释，加速读者的理解和学习，也为每章的技术点设置了大量的自测试题。最后以一个比较完整的、采用面向对象思想，以及通过MVC模式设计，并结合Smarty模板的CMS系统为案例，详细介绍了Web系统开发从设计到部署的各个细节，更好地进行开发实践。', '细说PHP', '[]', '0'), ('1534620731', '1', '78', '[\"\\u52d2\\u9053\\u592b\"]', '编程', 'https://img1.doubanio.com/lpic/s2393468.jpg', '9787121038709', '521', null, '2009-3', '电子工业出版社', '', '《PHP程序设计(第2版)》是最新版本PHP 5的权威指南，其中包含创建者PHP的创建者 Rasmus Lerdorf的独到的见解。《PHP程序设计(第2版)》以一种清晰而简练的风格介绍了PHP语言的语法和程序设计技术，并通过大量的示例演示了它们的正确使用方法和习惯用法。《PHP程序设计(第2版)》还给出了设计风格提示和实际的程序设计建议，这些将帮助你不仅成为一个PHP程序员，而且将是出色的PHP程序员。', 'PHP程序设计', '[\"\\u9648\\u6d69\", \"\\u80e1\\u4e39\", \"\\u5f90\\u666f\"]', '0'), ('1534620731', '1', '79', '[\"Kevin McArthur\"]', '编程', 'https://img1.doubanio.com/lpic/s3811388.jpg', '9787115193179', '300', null, '2009.7', '人民邮电出版社出版', '模式、框架与测试', '今天，PHP已经是无可争议的Web开发主流语言。PHP 5以后，它的面向对象特性也足以与Java和C#相抗衡。然而，讲述PHP高级特性的资料一直缺乏，大大影响了PHP语言的深入应用。\\n本书填补了这一空白。它专门针对有一定经验的PHP程序员，详细讲解了对他们最为重要的主题：高级面向对象、设计模式、文档、测试和标准PHP库等内容。同时，为适应目前Web开发的新趋势，作者还全面探讨了MVC架构和Zend Framework，以及Ajax、JSON、SOAP等Web 2.0技术。书中穿插的大量软件开发实战经验和对PHP编程思想的感悟，将大大提升你的内在功力。', 'PHP高级程序设计', '[\"\\u6c6a\\u6cf3\\u3000\\u7b49\"]', '0'), ('1534620731', '1', '80', '[\"Sara Golemon\"]', '编程', 'https://img3.doubanio.com/lpic/s1918674.jpg', '9780672327049', '456', null, '2006-6-9', 'Sams', '', 'In just a few years PHP has rapidly evolved from a small niche language to a powerful web development tool. Now in use on over 14 million Web sites, PHP is more stable and extensible than ever. However, there is no documentation on how to extend PHP; developers seeking to build PHP extensions and increase the performance and functionality of their PHP applications are left to word of mouth and muddling through PHP internals without systematic, helpful guidance. Although the basics of extension writing are fairly easy to grasp, the more advanced features have a tougher learning curve that can be very difficult to overcome. This is common at any moderate to high-traffic site, forcing the company hire talented, and high-priced, developers to increase performance. With Extending and Embedding PHP, Sara Golemon makes writing extensions within the grasp of every PHP developer, while guiding the reader through the tricky internals of PHP.', 'Extending and Embedding PHP', '[]', '0'), ('1534620731', '1', '81', '[\"\\u5384\\u5c14\\u66fc\"]', '编程', 'https://img3.doubanio.com/lpic/s26278810.jpg', '9787115156877', '499', null, '2007-5', '人民邮电出版社', '', '《PHP与MySQL基础教程(第2版)》采用基于任务的方法来讲授PHP和MySQL，使用大量图片指导读者深入学习语言，并向读者展示了如何构造Web站点。用简洁、直观的步骤和讲解提供了学习任务和概念的最快方式。通过《PHP与MySQL基础教程(第2版)》，读者可以快速、高效地学习PHP和MySQL，并可以立刻成为一位构筑Web站点的高手！', 'PHP与MySQL基础教程', '[\"\\u9648\\u5b97\\u658c\"]', '0'), ('1534620731', '1', '82', '[\"David Sklar\"]', '程序', 'https://img3.doubanio.com/lpic/s4245081.jpg', '9780596005603', '368', null, 'July, 2004', 'O\'Reilly', 'PHP 5', 'Learning PHP 5 is the ideal tutorial for graphic designers, bloggers, and other web crafters who want a thorough but non-intimidating way to understand the code that makes web sites dynamic. The book begins with an introduction to PHP, then moves to more advanced features: language basics, arrays and functions, web forms, connecting to databases, and much more. Complete with exercises to make sure the lessons stick, this book offers the ideal classroom learning experience whether you\'re in a classroom or on your own.', 'Learning PHP 5', '[]', '0'), ('1534620731', '1', '83', '[\"[\\u6fb3] Hugh E.Williams\", \"David Lane\"]', 'web', 'https://img1.doubanio.com/lpic/s5793818.jpg', '9787564105778', '782', null, '2006-11', '东南大学出版社', '', '有很多原因需要网站能够支持动态内容，包括：提供在线购物网站、创建个性化页面、管理来自数据库的大量信息等。只要拥有基础的html与网站管理知识，任何人都能学习php与mysql从而创建动态网站。这本书的目的是传授创建动态网站所需的背景知识，并使用这些工具架设安全且稳定的网站。\\n《php & mysql web数据库应用开发指南（第二版）》详细反映实际程序的需求，仔细地探讨外部数据的验证（例如信用卡卡号的格式）、用户登录以及如何使用模板建立网页的标准外观。本书的内容不仅仅是这些。书中还提到如何串联javascript与php让用户操作时更快、更方便。还有正确处理用户输入错误的方法，让网站看起来更专业。另外还引入大量来自pear外挂函数库的强大功能，对常用的、强大的包进行了详细说明。\\n本书虽是写给程序设计新手的指南，却未遗漏网站需要的重要技巧。凡是需要更新数据的网站，都会面对多位用户同时访问数据的问题；用户按下“刷新”按钮时，有可能导致新增相同的记录到数据库中……我们对各种类型的问题都提供了相关解决方案。\\n书中强调优秀的设计观念，像程序逻辑与页面显示分离就是一例。本书介绍php 5与mysql 4.1的功能，同时也提供适用于旧版程序的技巧。pear包也是本书的特色之一。包括模板、独立于数据库之外的api等数种重大包均频频出现于整合范例中，并附有详细说明。', 'PHP & MySQL Web数据库应用开发指南', '[\"O\\u2019Reilly Taiwan \\u516c\\u53f8\"]', '0'), ('1534620731', '1', '84', '[\"Dagfinn Reiersol\", \"Marcus Baker\", \"Chris Shiflett\"]', 'web', 'https://img3.doubanio.com/lpic/s4101120.jpg', '9787115217455', '444', null, '2010-01', '人民邮电出版社', '', '“对于那些想要在PHP方面更进一步的开发者而言，此书必不可少。”\\n——Gabriel Malkas， Developpez.com\\n“简而言之，这是我所读过的关于面向对象编程和PHP最好的图书。……强烈推荐此书，绝不要错过！”\\n——Amazon评论\\n“此书是理论与实践的完美融合，到目前为止，其他任何图书都无法与它相媲美。如果5颗星是满分，它完全值得10颗星!”\\n——Amazon评论\\n近几年，PHP的发展突飞猛进，业已超越Java和ASP，成为目前最流行的Web开发语言。而随着PHP 5的发布和Zend框架项目的应用，PHP和敏捷思想、设计模式以及单元测试的联姻成为主流话题。\\n本书首先介绍了面向对象设计的原则、模式和技巧，然后讲述了如何在PHP上利用其语法和特性，处理和实施这些原则、模式和技巧，并应用于Web编程中遇到的难题上。通过作者生动的叙述，读者可以学习到先进的面向对象实践、理论和技巧，对PHP有更深层、更透彻的理解，极大地提升自己面向对象设计和编程能力。', 'PHP实战', '[\"\\u5f20\\u9896 \\u7b49\", \"\\u6bb5\\u5927\\u4e3a \\u5ba1\\u6821\"]', '0'), ('1534620731', '1', '85', '[\"\\u5384\\u5c14\\u66fc\"]', 'web', 'https://img3.doubanio.com/lpic/s6633933.jpg', '9787115216786', '374', null, '2010-1', '人民邮电出版社', '第3版', '《PHP基础教程(第3版)》非常通俗易懂地向初学者介绍了PHP语言的基本概念、使用方法和注意事项。全书通过丰富的示例，引领读者逐步掌握这门流行的Web开发语言，使读者能够上手亲自编写适用于常用场景的PHP脚本。《PHP基础教程(第3版)》适合有基本的HTML经验的读者阅读。\\n点击链接进入新版：  Web开发系列:PHP基础教程(第4版)', 'PHP基础教程', '[\"\\u8d3e\\u83e1\", \"\\u5218\\u5f66\\u535a\"]', '0'), ('1534620731', '1', '86', '[\"\\u6cfd\\u74e6\\u65af\"]', 'web', 'https://img3.doubanio.com/lpic/s9114674.jpg', '9787115187314', '475', null, '2008-10-1', '人民邮电出版社', '图灵程序设计丛书', '本书通过一个完整的Web 2.0应用——带有动态图库、搜索和地图功能的博客系统详细介绍了Web开发的全过程。首先讨论了Web应用的规划与设计，然后逐章实现各个具体特性，包括网站主页、用户主页、用户注册页面、账户登录和管理页面、用户博客系统、网站搜索以及应用管理等，最后介绍部署和维护。\\n本书适合中、高级的PHP程序员阅读。', 'PHP Web 2.0开发实战', '[\"\\u82cf\\u91d1\\u56fd\"]', '0'), ('1534620731', '1', '87', '[\"\\u6234\\u7ef4\\u65af\"]', 'web', 'https://img3.doubanio.com/lpic/s5933963.jpg', '9787111240815', '386', null, '2008-8', '机械工业出版社', '', '《学习PHP和MySQL》内容全面，既解释了各种基础概念，又讨论了实现各种特定功能的具体细节，并附有示例应用来演示如何综合使用这些技术构建高效快速的Web站点。如果你有Web或图形设计的背景，且对HTML有一定了解，那么《学习PHP和MySQL》就正是你学习创建动态Web页面所需要的读物。\\n《学习PHP和MySQL》主要内容：\\nPHP基础，包括数据类型、程序流程控制、变量、函数、数组和表单。\\n在各种环境下安装PHP和IMySQL的步骤。\\nMySQL基础，比如表和SQL语句。\\n使用SQL操作数据库的简单介绍。\\n新增一章，介绍如何在PHlP中和网站上使用XHTML。\\n安全和访问控制，介绍常见的安全问题。\\n错误处理、HTTP身份认证和其他更多内容。', '学习PHP和MySQL', '[\"\\u8463\\u6653\\u864e\"]', '0'), ('1534620743', '1', '88', '[\"[\\u7f8e]BruceEckel\"]', '编程', 'https://img3.doubanio.com/lpic/s27243455.jpg', '9787111213826', '880', null, '2007-6', '机械工业出版社', '', '本书赢得了全球程序员的广泛赞誉，即使是最晦涩的概念，在Bruce Eckel的文字亲和力和小而直接的编程示例面前也会化解于无形。从Java的基础语法到最高级特性（深入的面向对象概念、多线程、自动项目构建、单元测试和调试等），本书都能逐步指导你轻松掌握。\\n从本书获得的各项大奖以及来自世界各地的读者评论中，不难看出这是一本经典之作。本书的作者拥有多年教学经验，对C、C++以及Java语言都有独到、深入的见解，以通俗易懂及小而直接的示例解释了一个个晦涩抽象的概念。本书共22章，包括操作符、控制执行流程、访问权限控制、复用类、多态、接口、通过异常处理错误、字符串、泛型、数组、容器深入研究、Java I/O系统、枚举类型、并发以及图形化用户界面等内容。这些丰富的内容，包含了Java语言基础语法以及高级特性，适合各个层次的Java程序员阅读，同时也是高等院校讲授面向对象程序设计语言以及Java语言的绝佳教材和参考书。\\n第4版特点：\\n适合初学者与专业人员的经典的面向对象叙述方式，为更新的Java SE5/6增加了新的示例和章节。\\n 测验框架显示程序输出。', 'Java编程思想 （第4版）', '[\"\\u9648\\u660a\\u9e4f\"]', '0'), ('1534620743', '1', '89', '[\"[\\u7f8e]JoshuaBloch\"]', '编程', 'https://img3.doubanio.com/lpic/s3479802.jpg', '9787111255833', '287', null, '2009-1-1', '机械工业出版社', '', '本书介绍了在Java编程中78条极具实用价值的经验规则，这些经验规则涵盖了大多数开发人员每天所面临的问题的解决方案。通过对Java平台设计专家所使用的技术的全面描述，揭示了应该做什么，不应该做什么才能产生清晰、健壮和高效的代码。\\n本书中的每条规则都以简短、独立的小文章形式出现，并通过例子代码加以进一步说明。本书内容全面，结构清晰，讲解详细。可作为技术人员的参考用书。', 'Effective java 中文版（第2版）', '[\"\\u4fde\\u9ece\\u654f\"]', '0'), ('1534620743', '1', '90', '[\"[\\u7f8e] \\u970d\\u65af\\u7279\\u66fc\", \"[\\u7f8e] \\u79d1\\u5948\\u5c14\"]', '编程', 'https://img3.doubanio.com/lpic/s1815135.jpg', '9787111185239', '691', null, '2006-5', '机械工业出版社', '基础知识', '本书是Java技术经典参考书，多年畅销不衰，第7版在保留以前版本风格的基础上，涵盖Java2开发平台标准版J2SE5.0的基础知识，主要内容包括面各对象程序设计、反射与代理、接口与内部类、事件监听器模型、使用Swing UI工具箱进行图形用户界面设计，异常处理、流输入/输出和对象序列化、泛型程序设计等。\\n本书内容翔实、深入浅出，附有大量程序实例，极具实用价值，是Java初学者和Java程序员的必备参考书。', 'JAVA 2核心技术 卷Ⅰ', '[\"\\u53f6\\u4e43\\u6587\", \"\\u909d\\u52b2\\u7b60 \\u7b49\"]', '0'), ('1534620743', '1', '91', '[\"[\\u7f8e]\\u6587\\u7eb3\\u65af\"]', '编程', 'https://img1.doubanio.com/lpic/s1106498.jpg', '9787111128052', '461', null, '2003-9', '机械工业出版社', '', '本书作者曾因本书荣获专业技术杂志《Java Report》评选的优秀作者奖，细心的读者可以从网上找到许多对本书第1版的赞誉。作者以易于理解的方式深入揭示了Java虚拟机的内部工作原理，深入理解这些内容，将对读者更快速地编写更高效的程序大有裨益！\\n本书共分20章，第1-4章解释了Java虚拟机的体系结构，包括Java栈、堆、方法区、执行引擎等；第5-20章深入描述了Java技术的内部细节，包括垃圾收集、Java安全模型、Java的连接模型和动态扩展机制、class文件、运算及流程控制等等，其中第6章和附录A-C安全可以作为class文件和指令集的参考手册。本书还附带光盘，光盘中包含用以辅助说明正文内容的交互式例示applet及示例源代码。', '深入Java虚拟机(原书第2版)', '[\"\\u66f9\\u6653\\u94a2\", \"\\u848b\\u9756\"]', '0');
INSERT INTO `book` VALUES ('1534620743', '1', '92', '[\"Michael Morrison\"]', '编程', 'https://img3.doubanio.com/lpic/s4813892.jpg', '9780596527747', '650', null, 'December 2007', 'O\'Reilly', '', 'Want to make the leap from writing HTML and CSS web pages and create dynamic web applications? Want to take your web skills to the next level? It sounds like you\'re ready to learn the Web\'s hottest programming language: JavaScript. Head First JavaScript is your ticket to going beyond copying and pasting the code from someone else\'s web site, and writing your own interactive web pages.\\nSo you\'re ready to make the leap from writing HTML and CSS web pages to creating dynamic web applications. You want to take your web skills to the next level. And you\'re finally ready to add \"programmer\" to the resume. It sounds like you\'re ready to learn the Web\'s hottest programming language: JavaScript. Head First JavaScript is your ticket to going beyond copying and pasting the code from someone else\'s web site, and writing your own interactive web pages.\\nWith Head First JavaScript, you learn:\\nThe basics of programming, from variables to types to looping\\nHow the web browser runs your code, and how you can talk to the browser with your code\\nWhy you\'ll never have to worry about casting, overloading, or polymorphism when you\'re writing JavaScript code\\nHow to use the Document Object Model to change your web pages without making your users click buttons\\nIf you\'ve ever read a Head First book, you know what to expect -- a visually rich format designed for the way your brain works. Head First JavaScript is no exception. It starts where HTML and CSS leave off, and takes you through your first program into more complex programming concepts -- like working directly with the web browser\'s object model and writing code that works on all modern browsers.\\nDon\'t be intimidated if you\'ve never written a line of code before! In typical Head First style, Head First JavaScript doesn\'t skip steps, and we\'re not interested in having you cut and paste code. You\'ll learn JavaScript, understand it, and have a blast along the way. So get ready... dynamic and exciting web pages are just pages away.', 'Head First JavaScript', '[]', '0'), ('1534620743', '1', '93', '[\"[\\u6377] Jaroslav Tulach\"]', '编程', 'https://img1.doubanio.com/lpic/s4656028.jpg', '9787115248497', '388', null, '2011-3', '人民邮电出版社', '', '本书帮助你解决API 设计方面的问题，共分3 个部分，分别指出学习API 设计是需要进行科学的训练的、Java 语言在设计方面的理论及设计和维护API 时的常见情况，并提供了各种技巧来解决相应的问题。\\n本书作者是NetBeans 的创始人，也是NetBeans 项目最初的架构师。相信在API 设计中遇到问题时，本书将不可或缺。\\n本书适用于软件设计人员阅读。', '软件框架设计的艺术', '[\"\\u738b\\u78ca\", \"\\u6731\\u5174\"]', '0'), ('1534620743', '1', '94', '[\"\\uff08\\u7f8e\\uff09Peter Haggar\"]', '编程', 'https://img1.doubanio.com/lpic/s1480989.jpg', '9787508307398', '278', null, '2003-1', '中国电力出版社', '中文版', '本书出自第一线JAVA编程专家之手，为读者展现正确、高效、强固之JAVA代码实践方案。\\n这本取材广泛的掼，以不同层级的专家经验，帮助程序员更透彻地理解JAVA，使他们的编码效能发挥到极致。此书以准则/条款形式进行编排，探究改善代码的68个重要主题，提供精晰明快、精心撰写之示例和方案。所有示例和方案都带有详尽的解说，你可以将它们融入自身工作之中。这些编程和设计上的实践方案，描述了经验丰富的程序员实际运用之最佳实践手法。为方便中高阶JAVA程序员阅读，本书内容被精心安排成为各自独立的课程，因此你既可以从头通读至尾，也可以挑选独特专题加以精读。', 'Practical Java中文版', '[\"\\u4faf\\u6377\", \"\\u5218\\u6c38\\u4e39\"]', '0'), ('1534620743', '1', '95', '[\"\\u8521\\u5b66\\u955b\"]', '编程', 'https://img3.doubanio.com/lpic/s1403563.jpg', '9787505386600', '277', null, '2003-4', '电子工业出版社', '程序员的心声', '本书是一本散文集。作为一名资深程序设计师，作者走笔清新面独特，简练俏皮的文字下，是作者对工作，对人生的理性思考。书中收录的文章内容贴近程序员的生活，能令读者产生强烈共鸣。此外，书中的部分文章也以轻松的风格剖析了学习Java技术时的常见问题，并以专家眼光和经验推荐介绍了一批优秀的技术书籍，旨在帮助读者兴趣盎然地学习Java。', 'Java夜未眠', '[]', '0'), ('1534620743', '1', '96', '[\"\\u960e\\u5b8f \\u7f16\\u8457\"]', '编程', 'https://img3.doubanio.com/lpic/s1201195.jpg', '9787505380004', '1024', null, '2002-10', '电子工业出版社', '', '《Java与模式》是一本讲解设计原则以及最为常见的设计模式的实用教材，目的是为了工作繁忙的Java系统设计师提供一个快速而准确的设计原则和设计模式的辅导。全书分为55章，第一个章节讲解一个编编程模式，说明此模式的用意、结构，以及这一模式适合于什么样的情况等。每一个章节都附有多个例子和练习题，研习这些例子、完成这些练习题可以帮助读者更好地理解所讲的内容。大多数的章节都是相对独立的，读者可以从任何一章开始读起。为了满足不同程序的读者的需要，书中提供了一些标为“专题”的章节，不阅读这些章节，对阅读书中的标准章节没有影响。', 'Java与模式', '[]', '0'), ('1534620744', '1', '97', '[\"[\\u7f8e]\\u5c3c\\u53e4\\u62c9\\u65af\\u00b7\\u6cfd\\u5361\\u65af\"]', '编程', 'https://img3.doubanio.com/lpic/s8958650.jpg', '9787115275790', '748', null, '2012-3-29', '人民邮电出版社', '', '本书是JavaScript 超级畅销书的最新版。ECMAScript 5 和HTML5 在标准之争中双双胜出，使大量专有实现和客户端扩展正式进入规范，同时也为JavaScript 增添了很多适应未来发展的新特性。本书这一版除增加5 章全新内容外，其他章节也有较大幅度的增补和修订，新内容篇幅约占三分之一。全书从JavaScript 语言实现的各个组成部分——语言核心、DOM、BOM、事件模型讲起，深入浅出地探讨了面向对象编程、Ajax 与Comet 服务器端通信，HTML5 表单、媒体、Canvas（包括WebGL）及Web Workers、地理定位、跨文档传递消息、客户端存储（包括IndexedDB）等新API，还介绍了离线应用和与维护、性能、部署相关的最佳开发实践。本书附录展望了未来的API 和ECMAScript Harmony 规范。\\n本书适合有一定编程经验的Web 应用开发人员阅读，也可作为高校及社会实用技术培训相关专业课程的教材。', 'JavaScript高级程序设计（第3版）', '[\"\\u674e\\u677e\\u5cf0\", \"\\u66f9\\u529b\"]', '0'), ('1534620744', '1', '98', '[\"[\\u7f8e] Kyle Simpson\"]', '编程', 'https://img3.doubanio.com/lpic/s28033372.jpg', '9787115385734', '195', null, '2015-4', '人民邮电出版社', '', 'JavaScript语言有很多复杂的概念，但却用简单的方式体现出来（比如回调函数），因此，JavaScript开发者无需理解语言内部的原理，就能编写出功能全面的程序；就像收音机一样，你无需理解里面的管子和线圈都是做什么用的，只要会操作收音机上的按键，就可以收听你喜欢的节目。然而，JavaScript的这些复杂精妙的概念才是语言的精髓，即使是经验丰富的JavaScript开发者，如果没有认真学习也无法真正理解语言本身的特性。正是因为绝大多数人不求甚解，一遇到出乎意料的行为就认为是语言本身有缺陷，进而把相关的特性加入黑名单，久而久之就排除了这门语言的多样性，人为地使它变得不完整、不安全。\\n“你不知道的JavaScript”系列就是要让不求甚解的JavaScript开发者迎难而上，深入语言内部，弄清楚JavaScript每一个零部件的用途。本书介绍了该系列的两个主题：“作用域和闭包”以及“this和对象原型”。掌握了这些知识之后，无论什么技术、框架和流行词语，你都能轻松理解。', '你不知道的JavaScript（上卷）', '[\"\\u8d75\\u671b\\u91ce\", \"\\u6881\\u6770\"]', '0'), ('1534620744', '1', '99', '[\"DouglasCrockford\"]', '编程', 'https://img3.doubanio.com/lpic/s3651235.jpg', '9787121084379', '155', null, '2009-4', '电子工业出版社', '', '本书通过对JavaScript语言的分析，甄别出好的和坏的特性，从而提取出相对这门语言的整体而言具有更好的可靠性、可读性和可维护性的JavaScript的子集，以便你能用它创建真正可扩展的和高效的代码。\\n雅虎资深JavaScript架构师Douglas Crockford倾力之作。\\n向读者介绍如何运用JavaScript创建真正可扩展的和高效的代码。', 'JavaScript语言精粹', '[\"\\u8d75\\u6cfd\\u6b23\", \"\\u9122\\u5b66\\u9e4d\"]', '0'), ('1534620744', '1', '100', '[\"Brian Goetz\", \"Tim Peierls\", \"Joshua Bloch\", \"Joseph Bowbeer\", \"David Holmes\", \"Doug Lea\"]', '编程', 'https://img3.doubanio.com/lpic/s7663093.jpg', '9787111370048', '293', null, '2012-2', '机械工业出版社华章公司', '', '本书深入浅出地介绍了Java线程和并发，是一本完美的Java并发参考手册。书中从并发性和线程安全性的基本概念出发，介绍了如何使用类库提供的基本并发构建块，用于避免并发危险、构造线程安全的类及验证线程安全的规则，如何将小的线程安全类组合成更大的线程安全类，如何利用线程来提高并发应用程序的吞吐量，如何识别可并行执行的任务，如何提高单线程子系统的响应性，如何确保并发程序执行预期任务，如何提高并发代码的性能和可伸缩性等内容，最后介绍了一些高级主题，如显式锁、原子变量、非阻塞算法以及如何开发自定义的同步工具类。\\n本书适合Java程序开发人员阅读。', 'Java并发编程实战', '[\"\\u7ae5\\u4e91\\u5170\"]', '0'), ('1534620744', '1', '101', '[\"[\\u82f1] Jeremy Keith\", \"[\\u52a0] Jeffrey Sambells\"]', '编程', 'https://img3.doubanio.com/lpic/s4677623.jpg', '9787115249999', '300', null, '2011-4', '人民邮电出版社', '', 'JavaScript是Web开发中最重要的一门语言，它强大而优美。无论是桌面开发，还是移动应用。JavaScript都是必须掌握的技术。W3C的DOM标准是开发Web应用的基石。已经得到所有现代浏览器的支持，这使得跨平台Web开发成了一件轻松惬意的事。\\n本书是超级畅销书的升级版，由倡导Web标准的领军人物执笔，揭示了前端开发的真谛，是学习JavaScript和DOM开发的必读之作。\\n本 书在简洁明快地讲述JavaScript和DOM的基本知识之后，通过几个实例演示了专业水准的网页开发技术，透彻阐述了平稳退化等一批至关重要的 JavaScript编程原则和最佳实践，并全面探讨了HTML5以及jQuery等JavaScript库。读者将看到JavaScript、 HTML5和CSS如何协作来创建易用的、与标准兼容的Web设计，掌握使用JavaScript和DOM通过客户端动态效果和用户控制的动画来加强 Web页面的必备技术；同时，还将对如何利用库提高开发效率有全面深入的理解。', 'JavaScript DOM编程艺术 （第2版）', '[\"\\u6768\\u6d9b\", \"\\u738b\\u5efa\\u6865\", \"\\u6768\\u6653\\u4e91 \\u7b49\"]', '0'), ('1534620744', '1', '102', '[\"\\u5468\\u5fd7\\u660e\"]', '编程', 'https://img3.doubanio.com/lpic/s27458236.jpg', '9787111421900', '433', null, '2013-9-1', '机械工业出版社', 'JVM高级特性与最佳实践', '《深入理解Java虚拟机:JVM高级特性与最佳实践(第2版)》内容简介：第1版两年内印刷近10次，4家网上书店的评论近4?000条，98%以上的评论全部为5星级的好评，是整个Java图书领域公认的经典著作和超级畅销书，繁体版在台湾也十分受欢迎。第2版在第1版的基础上做了很大的改进：根据最新的JDK 1.7对全书内容进行了全面的升级和补充；增加了大量处理各种常见JVM问题的技巧和最佳实践；增加了若干与生产环境相结合的实战案例；对第1版中的错误和不足之处的修正；等等。第2版不仅技术更新、内容更丰富，而且实战性更强。\\n《深入理解Java虚拟机:JVM高级特性与最佳实践(第2版)》共分为五大部分，围绕内存管理、执行子系统、程序编译与优化、高效并发等核心主题对JVM进行了全面而深入的分析，深刻揭示了JVM的工作原理。\\n第一部分从宏观的角度介绍了整个Java技术体系、Java和JVM的发展历程、模块化，以及JDK的编译，这对理解书中后面内容有重要帮助。\\n第二部分讲解了JVM的自动内存管理，包括虚拟机内存区域的划分原理以及各种内存溢出异常产生的原因；常见的垃圾收集算法以及垃圾收集器的特点和工作原理；常见虚拟机监控与故障处理工具的原理和使用方法。\\n第三部分分析了虚拟机的执行子系统，包括类文件结构、虚拟机类加载机制、虚拟机字节码执行引擎。\\n第四部分讲解了程序的编译与代码的优化，阐述了泛型、自动装箱拆箱、条件编译等语法糖的原理；讲解了虚拟机的热点探测方法、HotSpot的即时编译器、编译触发条件，以及如何从虚拟机外部观察和分析JIT编译的数据和结果；\\n第五部分探讨了Java实现高效并发的原理，包括JVM内存模型的结构和操作；原子性、可见性和有序性在Java内存模型中的体现；先行发生原则的规则和使用；线程在Java语言中的实现原理；虚拟机实现高效并发所做的一系列锁优化措施。', '深入理解Java虚拟机（第2版）', '[]', '0'), ('1534620744', '1', '103', '[\"Kathy Sierra,Bert Bates \\u8457\", \"\\u6768\\u5c0a\\u4e00 \\u7f16\\u8bd1 \\u5f20\\u7136\\u7b49 \\u6539\\u7f16\"]', '编程', 'https://img3.doubanio.com/lpic/s2171906.jpg', '9787508344980', '600', null, '2007-2', '中国电力出版社', '', '《Head First Java》是本完整的面向对象（object-oriented，OO）程序设计和Java的学习指导。此书是根据学习理论所设计的，让你可以从学习程序语言的基础开始一直到包括线程、网络与分布式程序等项目。最重要的，你会学会如何像个面向对象开发者一样去思考。\\n而且不只是读死书，你还会玩游戏、拼图、解谜题以及以意想不到的方式与Java交互。在这些活动中，你会写出一堆真正的Java程序，包括了一个船舰炮战游戏和一个网络聊天程序。\\nHead First系列的图文并茂学习方式能让你快速地在脑海中掌握住知识。敞开心胸准备好学习这些关键性的主题：\\n*Java程序语言\\n*面向对象程序开发\\n*Swing图形化接口\\n*使用Java API函数库\\n*编写、测试与布署应用程序\\n*处理异常\\n*多线程\\n*网络程序设计\\n*集合与泛型\\n如果你想要看“一本正经”的书，去找其他的。但如果你真地想要好好地学习Java，你会需要《Head First Java》。这本书可是Amazon编辑推荐的十大好书之一！', 'Head First Java（第二版·中文版）', '[\"\\u6768\\u5c0a\\u4e00\"]', '0'), ('1534620744', '1', '104', '[\"\\uff08\\u7f8e\\uff09  Y. Daniel Liang\"]', '编程', 'https://img1.doubanio.com/lpic/s6520557.jpg', '9787111340812', '604', null, '2011-6', '机械工业出版社', '基础篇 (原书第8版)', '本书是Java语言的经典教材，多年来畅销不衰。本书全面整合了Java 6的特性，采用“基础优先，问题驱动”的教学方式，循序渐进地介绍了程序设计基础、解决问题的方法、面向对象程序设计、图形用户界面设计、异常处理、I/O和递归等内容。此外，本书还全面且深入地覆盖了一些高级主题，包括算法和数据结构、多线程、网络、国际化、高级GUI等内容。\\n本书中文版由《Java语言程序设计：基础篇》和《Java语言程序设计：进阶篇》组成。基础篇对应原书的第1～20章，进阶篇对应原书的第21～37章。\\n本书特点：\\n	基础篇介绍基础内容，进阶篇介绍高级内容，便于教师按需选择理想的教材。\\n	全面整合了Java 6的特性，对全书的内容进行了修订和更新，以反映Java程序设计方面的最新技术发展。\\n	对面向对象程序设计进行了深入论述，包含GUI程序设计的基础和扩展实例。\\n	提供的大量实例中都包括了对问题求解的详细步骤，很多实例都是随着Java技术的引入不断地进行增强，这种循序渐进的讲解方式更易于学生学习。\\n	较上一版增加了大量难易程度不同的习题，同时在作者的网站http://www.cs.armstrong.edu/liang/intro8e/index.html 中还提供了很多自测题。\\n	为满足对Web设计有浓厚兴趣的同学，本版在配套网站上增加了第39～48章的内容，以提供更多的相关信息。', 'Java语言程序设计', '[\"\\u674e\\u5a1c\"]', '0'), ('1534620744', '1', '105', '[\"\\u66fe\\u63a2\"]', '编程', 'https://img3.doubanio.com/lpic/s28065006.jpg', '9787115388889', '294', null, '2015-5', '人民邮电出版社', '', '本书在尊重《设计模式》原意的同时，针对JavaScript语言特性全面介绍了更适合JavaScript程序员的了16个常用的设计模式，讲解了JavaScript面向对象和函数式编程方面的基础知识，介绍了面向对象的设计原则及其在设计模式中的体现，还分享了面向对象编程技巧和日常开发中的代码重构。本书将教会你如何把经典的设计模式应用到JavaScript语言中，编写出优美高效、结构化和可维护的代码。', 'JavaScript设计模式与开发实践', '[]', '0'), ('1534620744', '1', '106', '[\"[\\u7f8e] Kyle Simpson\"]', '编程', 'https://img3.doubanio.com/lpic/s28969600.jpg', '9787115431165', '358', null, '2016-8', '人民邮电出版社', '', 'JavaScript这门语言简单易用，很容易上手，但其语言机制复杂微妙，即使是经验丰富的JavaScript开发人员，如果没有认真学习的话也无法真正理解。本套书直面当前JavaScript开发人员不求甚解的大趋势，深入理解语言内部的机制，全面介绍了JavaScript中常被人误解和忽视的重要知识点。本书是其中卷，主要介绍了类型、语法、异步和性能。', '你不知道的JavaScript（中卷）', '[\"\\u5355\\u4e1a\", \"\\u59dc\\u5357\"]', '0'), ('1534620744', '1', '107', '[\"\\u624e\\u5361\\u65af\"]', '编程', 'https://img3.doubanio.com/lpic/s25808235.jpg', '9787115310088', '226', null, '2013-4', '人民邮电出版社', '', '《编写可维护的JavaScript》向开发人员阐述了如何在团队开发中编写具备高可维护性的JavaScript代码，书中详细说明了作为团队一分子，应该怎么写JavaScript。《编写可维护的JavaScript》内容涵盖了编码风格、编程技巧、自动化、测试等几方面，既包括具体风格和原则的介绍，也包括示例和技巧说明，最后还介绍了如何通过自动化的工具和方法来实现一致的编程风格。\\n《编写可维护的JavaScript》适合前端开发工程师、JavaScript程序员和学习JavaScript编程的读者阅读，也适合开发团队负责人、项目负责人阅读。', '编写可维护的JavaScript', '[\"\\u674e\\u6676\", \"\\u90ed\\u51ef\", \"\\u5f20\\u6563\\u96c6\"]', '0');
COMMIT;

-- ----------------------------
--  Table structure for `classic`
-- ----------------------------
DROP TABLE IF EXISTS `classic`;
CREATE TABLE `classic` (
  `create_time` int(11) DEFAULT NULL,
  `status` smallint(6) DEFAULT NULL,
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '期刊在数据中序号，供点赞使用',
  `content` text COMMENT '期刊内容',
  `img_id` int(11) DEFAULT NULL,
  `index` smallint(6) DEFAULT NULL COMMENT '期号',
  `title` varchar(100) DEFAULT NULL COMMENT '期刊题目',
  `author` varchar(100) DEFAULT NULL COMMENT '作者',
  `type` smallint(6) DEFAULT NULL COMMENT '期刊类型,这里的类型分为:100 电影 200 音乐 300 句子',
  `url` varchar(100) DEFAULT NULL COMMENT '当type为300时，此字段为音乐url',
  PRIMARY KEY (`id`),
  KEY `img_id` (`img_id`),
  CONSTRAINT `classic_ibfk_1` FOREIGN KEY (`img_id`) REFERENCES `image` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=9 DEFAULT CHARSET=utf8mb4 ROW_FORMAT=COMPACT;

